INSERT INTO `tbl_baiviet` (`id`, `tenbaiviet`, `tomtat`, `noidung`, `id_danhmuc`, `tinhtrang`, `hinhanh`) VALUES
(3, 'Sách về phi công lái MiG-21 chiến đấu năm 1972', '<p>Cựu phi c&ocirc;ng m&aacute;y bay ti&ecirc;m k&iacute;ch Nguyễn C&ocirc;ng Huy ra truyện k&yacute; về cuộc chiến tr&ecirc;n bầu trời H&agrave; Nội v&agrave; miền Bắc cuối năm 1972.&nbsp;</p>\r\n', '<p>Cựu phi c&ocirc;ng m&aacute;y bay ti&ecirc;m k&iacute;ch Nguyễn C&ocirc;ng Huy ra truyện k&yacute; về cuộc chiến tr&ecirc;n bầu trời H&agrave; Nội v&agrave; miền Bắc cuối năm 1972.<br />\r\nS&aacute;ch Người l&iacute;nh phi c&ocirc;ng kể chuyện khắc họa tr&iacute; tuệ, bản lĩnh của người l&iacute;nh Kh&ocirc;ng qu&acirc;n Nh&acirc;n d&acirc;n Việt Nam, t&igrave;nh đồng đội, những hy sinh v&agrave; sự khốc liệt của chiến tranh.<br />\r\nNội dung được chia l&agrave;m hai phần: H&agrave;nh tr&igrave;nh trở th&agrave;nh phi c&ocirc;ng của Nguyễn C&ocirc;ng Huy v&agrave; đồng đội, chiến dịch Điện Bi&ecirc;n Phủ tr&ecirc;n kh&ocirc;ng 12 ng&agrave;y đ&ecirc;m.&nbsp;<br />\r\nKh&ocirc;ng chỉ mi&ecirc;u tả chi tiết c&aacute;c cuộc chiến, s&aacute;ch c&ograve;n mang tới g&oacute;c nh&igrave;n, suy nghĩ, cảm x&uacute;c của người l&iacute;nh, đưa độc giả v&agrave;o thế giới t&agrave;n khốc nhưng vẫn chứa đựng t&igrave;nh y&ecirc;u v&agrave; hy vọng.<br />\r\nC&acirc;u chuyện bắt đầu từ thời điểm Nguyễn C&ocirc;ng Huy rời l&agrave;ng qu&ecirc; v&agrave; trường phổ th&ocirc;ng để nhập ngũ. Anh l&agrave; một trong số c&aacute;c chiến sĩ được cử đi Li&ecirc;n X&ocirc; học l&aacute;i m&aacute;y bay chiến đấu. Vượt qua nhiều kh&oacute; khăn của cuộc sống học tập nơi xa xứ, c&aacute;c học vi&ecirc;n bay trẻ tuổi đồng l&ograve;ng quyết t&acirc;m l&agrave;m chủ m&aacute;y bay v&agrave; bầu trời, t&ocirc;i luyện &yacute; ch&iacute; để trở về qu&ecirc; nh&agrave; chiến đấu bảo vệ Tổ quốc.</p>\r\n\r\n<p>Phần sau l&agrave; kh&ocirc;ng kh&iacute; chiến đấu của những phi c&ocirc;ng ti&ecirc;m k&iacute;ch Việt Nam, đặc biệt l&agrave; đội bay đ&ecirc;m. Qua lời kể của t&aacute;c giả Nguyễn C&ocirc;ng Huy, s&aacute;ch ph&aacute;c họa sự &aacute;c liệt tr&ecirc;n bầu trời miền Bắc khi lực lượng kh&ocirc;ng qu&acirc;n ti&ecirc;m k&iacute;ch non trẻ đối đầu kh&ocirc;ng qu&acirc;n Mỹ.</p>\r\n\r\n<p>Từng chuyến xuất k&iacute;ch, từng cuộc chạm tr&aacute;n tr&ecirc;n bầu trời cho thấy phi c&ocirc;ng m&aacute;y bay ti&ecirc;m k&iacute;ch phải đối mặt nhiều nguy hiểm: &quot;Chiến trường tr&ecirc;n kh&ocirc;ng kh&ocirc;ng c&oacute; chiến h&agrave;o, m&aacute;y bay chỉ bay tiến, kh&ocirc;ng biết dừng, kh&ocirc;ng biết l&ugrave;i. Bầu trời m&ecirc;nh m&ocirc;ng kh&ocirc;ng c&oacute; nơi ẩn nấp&quot;.</p>\r\n\r\n<p>T&aacute;c giả viết về điều kiện chiến đấu khi đ&oacute;: &quot;Cất c&aacute;nh kh&oacute; khăn một th&igrave; hạ c&aacute;nh kh&oacute; khăn gấp mười. Đường băng bị đ&aacute;nh hỏng, vỡ n&aacute;t v&agrave; đầy hố bom xung quanh kh&ocirc;ng biết sẽ lao xuống đ&oacute; l&uacute;c n&agrave;o. Trong mưa gi&oacute; mịt m&ugrave;ng, phi c&ocirc;ng phải t&igrave;m c&aacute;ch hạ c&aacute;nh m&agrave; kh&ocirc;ng hề c&oacute; hệ thống đ&egrave;n chiếu s&aacute;ng, kh&ocirc;ng c&oacute; chỉ huy&quot;.</p>\r\n\r\n<p>Trong thời gian chiến dịch, c&aacute;c phi c&ocirc;ng bay đ&ecirc;m vẫn quyết t&acirc;m cất c&aacute;nh bằng mọi gi&aacute; để ti&ecirc;u diệt B-52: &quot;C&oacute; nhiều chuyến xuất k&iacute;ch chiến đấu, khi cất c&aacute;nh l&ecirc;n đ&atilde; biết l&uacute;c về sẽ kh&ocirc;ng thể hạ c&aacute;nh nổi v&igrave; thời tiết rất xấu, c&oacute; thể sẽ phải bỏ m&aacute;y bay, nhảy d&ugrave;. Thế nhưng, kh&ocirc;ng một ai ch&ugrave;n bước, vẫn đưa được m&aacute;y bay l&ecirc;n trời, vẫn đi chiến đấu&quot;.</p>\r\n\r\n<p>Trong 12 ng&agrave;y cuối năm 1972 (18-29/12), qu&acirc;n đội Việt Nam đ&atilde; đ&aacute;nh bại cuộc tập k&iacute;ch đường kh&ocirc;ng chiến lược bằng m&aacute;y bay B-52 của Mỹ tr&ecirc;n bầu trời miền Bắc, lập n&ecirc;n chiến thắng H&agrave; Nội - Điện Bi&ecirc;n Phủ tr&ecirc;n kh&ocirc;ng. Nhờ vậy, Mỹ buộc phải k&yacute; Hiệp định Paris về chấm dứt chiến tranh, lập lại h&ograve;a b&igrave;nh ở Việt Nam.</p>\r\n\r\n<p>Chiến dịch thắng lợi nhờ sự đ&oacute;ng g&oacute;p lớn của Lực lượng Kh&ocirc;ng qu&acirc;n Ti&ecirc;m k&iacute;ch thuộc Qu&acirc;n chủng Ph&ograve;ng kh&ocirc;ng - Kh&ocirc;ng qu&acirc;n. Từng chiến c&ocirc;ng ti&ecirc;u biểu của những anh h&ugrave;ng bắn rơi B-52 như Phạm Tu&acirc;n, Vũ Xu&acirc;n Thiều lần lượt được t&aacute;c giả kể lại với nhiều cung bậc cảm x&uacute;c.<br />\r\nT&aacute;c giả Nguyễn C&ocirc;ng Huy sinh năm 1947 tại Thường T&iacute;n, H&agrave; Nội. &Ocirc;ng l&agrave; cựu phi c&ocirc;ng ti&ecirc;m k&iacute;ch MiG-21 của Trung đo&agrave;n Kh&ocirc;ng qu&acirc;n Sao Đỏ. Nguyễn C&ocirc;ng Huy l&agrave; một trong c&aacute;c phi c&ocirc;ng MiG-21 được đ&agrave;o tạo tại Li&ecirc;n X&ocirc; từ năm 1965, sau đ&oacute; về nước tham gia lực lượng Kh&ocirc;ng qu&acirc;n Ti&ecirc;m k&iacute;ch trong cuộc kh&aacute;ng chiến chống Mỹ cứu nước.</p>\r\n', 5, 1, '1678353969_1.jpg'),
(4, 'Sách chỉ dẫn khi gặp vấn đề tâm lý', '<p>&quot;C&oacute; lẽ bạn n&ecirc;n gặp b&aacute;c sĩ t&acirc;m l&yacute;&quot; cung cấp gợi &yacute; khi đối mặt căng thẳng trong cuộc sống hiện đại, cần trợ gi&uacute;p về tinh thần.&nbsp;</p>\r\n', '<p>&quot;C&oacute; lẽ bạn n&ecirc;n gặp b&aacute;c sĩ t&acirc;m l&yacute;&quot; cung cấp gợi &yacute; khi đối mặt căng thẳng trong cuộc sống hiện đại, cần trợ gi&uacute;p về tinh thần.</p>\r\n\r\n<p>S&aacute;ch của chuy&ecirc;n gia t&acirc;m l&yacute; nổi tiếng người Mỹ Lori Gottlieb (t&ecirc;n tiếng Anh l&agrave;&nbsp;<em>Maybe You Should Talk To Someone</em>, Nh&agrave; xuất bản Thế giới ph&aacute;t h&agrave;nh, Quỳnh Anh v&agrave; B&iacute;ch Nhan dịch) đem lại những giải đ&aacute;p hữu &iacute;ch cho c&acirc;u hỏi: Bao giờ bạn cần gặp b&aacute;c sĩ?</p>\r\n\r\n<p>Lori Gottlieb lần lượt kể c&aacute;c c&acirc;u chuyện về bốn bệnh nh&acirc;n ti&ecirc;u biểu: Một nh&agrave; bi&ecirc;n kịch c&oacute; t&agrave;i tại Hollywood nhưng gặp nhiều căng thẳng trong cuộc sống gia đ&igrave;nh cũng như sự nghiệp, một giảng vi&ecirc;n trẻ mới cưới được chẩn đo&aacute;n mắc ung thư di căn, một người trẻ bị nghiện rượu, một phụ nữ dự định kết liễu cuộc đời v&agrave;o sinh nhật 70 tuổi nếu cuộc sống kh&ocirc;ng c&oacute; g&igrave; tốt hơn, v&agrave; sự suy sụp của ch&iacute;nh t&aacute;c giả khi bị từ h&ocirc;n ở tuổi 40.</p>\r\n\r\n<p>Qua những c&acirc;u chuyện đan xen trong từng chương s&aacute;ch, độc giả lần lượt được kh&aacute;m ph&aacute; g&oacute;c khuất s&acirc;u k&iacute;n trong đời sống của cả năm người. Cho đến cuối cuốn s&aacute;ch cũng l&agrave; cuối qu&aacute; tr&igrave;nh trị liệu, khi mỗi nh&acirc;n vật đều hiểu bản th&acirc;n hơn, vượt qua những đấu tranh c&aacute; nh&acirc;n, chữa l&agrave;nh thương tổn v&agrave; t&igrave;m lại được niềm vui sống. Độc giả cũng được cung cấp nhiều kiến thức, lời khuy&ecirc;n để c&oacute; thể &aacute;p dụng v&agrave;o cuộc sống tinh thần của ch&iacute;nh m&igrave;nh.</p>\r\n\r\n<p>T&aacute;c giả v&agrave; bốn bệnh nh&acirc;n của c&ocirc; trải qua những t&igrave;nh huống kh&aacute;c nhau nhưng độc giả thấy giữa họ c&oacute; những điểm tương đồng cơ bản. C&acirc;u chuyện họ chia sẻ ban đầu thường kh&ocirc;ng phải l&agrave; vấn đề cần giải quyết. Chỉ khi bệnh nh&acirc;n c&ugrave;ng nh&agrave; trị liệu kh&aacute;m ph&aacute; ra nguy&ecirc;n nh&acirc;n s&acirc;u xa, qu&aacute; tr&igrave;nh trị liệu của họ mới c&oacute; những bước tiến vượt bậc.</p>\r\n\r\n<p>Gottlieb gi&uacute;p người đọc hiểu r&otilde; c&aacute;ch c&aacute;c nh&agrave; trị liệu được đ&agrave;o tạo, c&aacute;ch họ học v&agrave; thay đổi lối sống do nghề nghiệp, c&aacute;ch họ cố gắng hết sức để gi&uacute;p đỡ bệnh nh&acirc;n. T&aacute;c giả cho ch&uacute;ng ta thấy c&aacute;c nh&agrave; trị liệu cũng c&oacute; vấn đề ri&ecirc;ng v&agrave; nhiều khi họ cũng cần trị liệu.</p>\r\n\r\n<p>Amy Dickinson, b&igrave;nh luận vi&ecirc;n chuy&ecirc;n mục&nbsp;<em>Ask Amy</em>&nbsp;v&agrave; t&aacute;c giả s&aacute;ch b&aacute;n chạy theo&nbsp;<em>News York Times - Strangers Tend to Tell Me</em>&nbsp;Things nhận x&eacute;t: &quot;Đ&acirc;y l&agrave; cuốn s&aacute;ch tinh tế, dịu d&agrave;ng, truyền cảm hứng v&agrave; h&agrave;i hước. Lori Gottlieb đ&atilde; dũng cảm dẫn dắt người đọc tham gia h&agrave;nh tr&igrave;nh đi v&agrave;o ch&iacute;nh m&igrave;nh, mở ra cho ch&uacute;ng ta qu&aacute; tr&igrave;nh trị liệu từ cả hai ph&iacute;a của chiếc ghế sofa - nh&agrave; trị liệu v&agrave; bệnh nh&acirc;n&quot;.</p>\r\n\r\n<p>Với hơn một triệu bản in được b&aacute;n ra t&iacute;nh đến hiện tại,&nbsp;<em>C&oacute; lẽ bạn n&ecirc;n gặp b&aacute;c sĩ t&acirc;m l&yacute;</em>&nbsp;nằm trong nhiều danh s&aacute;ch s&aacute;ch b&aacute;n chạy tr&ecirc;n thế giới như:&nbsp;<em>The New York Times Best Seller, Amazon&rsquo;s Best Seller, The Oprah Magazine&#39;s Best Nonfiction Book of 2019, Book Riot Most Anticipated Book of 2019...</em></p>\r\n', 5, 1, '1678355334_2.jpg'),
(5, 'Nhà báo kể chuyện Trên những dặm đường', '<p>S&aacute;ch ảnh k&egrave;m ghi ch&eacute;p &quot;Tr&ecirc;n những dặm đường&quot; của nh&agrave; b&aacute;o Đức Li&ecirc;n đưa độc giả qua nhiều quốc gia với c&aacute;c c&acirc;u chuyện văn h&oacute;a.&nbsp;</p>\r\n', '<p>S&aacute;ch ảnh k&egrave;m ghi ch&eacute;p &quot;Tr&ecirc;n những dặm đường&quot; của nh&agrave; b&aacute;o Đức Li&ecirc;n đưa độc giả qua nhiều quốc gia với c&aacute;c c&acirc;u chuyện văn h&oacute;a.</p>\r\n\r\n<p>Mở đầu ấn phẩm bằng d&ograve;ng tản mạn về nước Mỹ, t&aacute;c giả Đức Li&ecirc;n g&oacute;i gh&eacute;m hồi ức lần đầu đặt ch&acirc;n đến thủ đ&ocirc; Washington v&agrave;o một ng&agrave;y m&ugrave;a thu, đến những lần trở lại Mỹ, qua nhiều th&agrave;nh phố, từ bờ Đ&ocirc;ng sang bờ T&acirc;y. 48 giờ ở &quot;thủ đ&ocirc;&quot; học thuật Boston, lần thưởng thức m&oacute;n phở Việt ở Arkansas hay khoảnh khắc tận mắt chi&ecirc;m ngưỡng tuyệt t&aacute;c Grand Canyon... C&oacute; chuyến đi t&aacute;c giả chỉ &quot;cưỡi ngựa xem hoa&quot;, c&oacute; chuyến anh đủ thời gian nhẩn nha, nh&igrave;n ngắm c&ocirc;ng tr&igrave;nh, kiến tr&uacute;c, h&ograve;a v&agrave;o cuộc sống hiện đại lẫn gần gũi thi&ecirc;n nhi&ecirc;n của quốc gia đa chủng tộc.</p>\r\n\r\n<p>Từ Mỹ, t&aacute;c giả trải nghiệm một ng&agrave;y vượt bi&ecirc;n giới đặt ch&acirc;n đến đất Mexico thưởng thức m&oacute;n s&uacute;p hầm Pozole, hay sang đất Canada dạo chơi phố cổ Qu&eacute;bec. C&aacute;c quốc gia ch&acirc;u &Acirc;u như T&acirc;y Ban Nha, Italy, Bồ Đ&agrave;o Nha, C&ocirc;ng h&ograve;a Czech, Đức... cũng được điểm danh qua trang viết. Kh&ocirc;ng chỉ dừng lại ở trời &Acirc;u, nh&agrave; b&aacute;o Đức Li&ecirc;n c&ograve;n tản mạn với kỷ niệm ăn Tết ở Nhật, đ&oacute;n giao thừa dưới bầu trời mưa ở th&agrave;nh cổ Toyama, ngắm tuyết rơi ở Sapporo, hay nghe tiếng v&oacute; ngựa l&oacute;c c&oacute;c tr&ecirc;n con đường đ&aacute; cuội ở Cuba.</p>\r\n\r\n<p>Ghi ch&eacute;p trong s&aacute;ch khi bằng g&oacute;c nh&igrave;n của một du kh&aacute;ch, thấy đ&acirc;u cũng lạ v&agrave; đẹp, khi bằng g&oacute;c nh&igrave;n của một nh&agrave; b&aacute;o lu&ocirc;n t&ograve; m&ograve;, quan s&aacute;t, g&oacute;p nhặt để tường thuật, ph&acirc;n t&iacute;ch, chia sẻ kinh nghiệm về con người, cuộc sống. Kh&ocirc;ng chỉ l&agrave; c&acirc;u chuyện tận hưởng những địa điểm nổi tiếng, sang trọng của thế giới, s&aacute;ch c&ograve;n viết về h&aacute;o hức của th&uacute; vui x&ecirc; dịch, những mẩu chuyện b&ecirc;n lề cuộc sống: Người cầm biển hiệu &quot;I&#39;m hungry&quot; (t&ocirc;i đ&oacute;i) ở thủ đ&ocirc; nước Mỹ, c&ocirc; g&aacute;i Việt kiều với giọng Nam bộ sẵn s&agrave;ng hướng dẫn cho kh&aacute;ch đồng hương hiểu th&ecirc;m về xứ người, những &quot;b&iacute; k&iacute;p&quot; bỏ t&uacute;i khi đến Cuba...</p>\r\n\r\n<p>Điểm nhấn của s&aacute;ch c&ograve;n l&agrave; h&agrave;ng chục bức ảnh do t&aacute;c giả s&aacute;ng t&aacute;c. Những điều con chữ chưa kể hết được n&oacute;i thay bằng nhiều bức ảnh khắc họa vẻ đẹp c&aacute;c c&ocirc;ng tr&igrave;nh kiến tr&uacute;c, phong cảnh, văn h&oacute;a ẩm thực. Ấn phẩm kh&eacute;p lại với dư &acirc;m nối d&agrave;i về ước mơ chinh phục những cung đường mới, thấy cuộc sống thật đẹp, để t&iacute;ch lũy kiến thức, mở rộng tầm nh&igrave;n, nu&ocirc;i dưỡng t&igrave;nh y&ecirc;u thi&ecirc;n nhi&ecirc;n c&ugrave;ng tr&aacute;i tim kh&aacute;t khao kh&aacute;m ph&aacute; thế giới.</p>\r\n\r\n<p>Nguyễn Đức Li&ecirc;n từng c&ocirc;ng t&aacute;c tại Đ&agrave;i Ph&aacute;t thanh - Truyền h&igrave;nh Đồng Nai, Đ&agrave;i tiếng n&oacute;i Việt Nam, B&agrave; Rịa - Vũng T&agrave;u, b&aacute;o Thanh Ni&ecirc;n, Vietnamnet. Sinh trưởng trong gia đ&igrave;nh c&oacute; truyền thống l&agrave;m b&aacute;o, anh l&agrave;m quen với m&ugrave;i mực in b&aacute;o giấy mỗi buổi sớm ng&agrave;y b&eacute;, nu&ocirc;i dưỡng ước mơ theo đuổi c&ocirc;ng việc viết l&aacute;ch. L&agrave;m quen m&aacute;y ảnh từ thời tiểu học, ghi ch&eacute;p v&agrave; chụp ảnh l&agrave; hai kỹ năng t&aacute;c giả lu&ocirc;n trui r&egrave;n cho bản th&acirc;n suốt 40 năm trải qua nhiều loại h&igrave;nh từ b&aacute;o giấy đến b&aacute;o điện tử. Theo t&aacute;c giả, m&oacute;n qu&agrave; lớn của đời l&agrave;m b&aacute;o l&agrave; những chuyến đi, c&aacute;c cuộc gặp gỡ. Anh từng c&oacute; cơ hội đến 40 quốc gia v&agrave; v&ugrave;ng l&atilde;nh thổ.&nbsp;<em>Tr&ecirc;n những dặm đường&nbsp;</em>l&agrave; cuốn s&aacute;ch thứ ba của anh, sau quyển&nbsp;<em>Lửa gi&agrave;n khoan&nbsp;</em>(NXB Trẻ, 2001),&nbsp;<em>Đến v&agrave; đi</em>&nbsp;(NXB Trẻ, 2008).</p>\r\n\r\n<p>T&aacute;c giả c&ograve;n tham gia giảng dạy b&aacute;o ch&iacute;. &quot;Khi tiếp x&uacute;c với sinh vi&ecirc;n, bạn trẻ muốn theo nghề, t&ocirc;i lu&ocirc;n chia sẻ l&agrave;m b&aacute;o trong thời buổi n&agrave;o cũng cần t&iacute;ch lũy kiến thức, dấn th&acirc;n v&agrave; đam m&ecirc;. Thời Internet, nh&agrave; b&aacute;o cần th&iacute;ch nghi nhanh với c&ocirc;ng nghệ, nắm bắt cập nhật để hiểu c&aacute;c xu hướng kỹ thuật, c&aacute;ch đưa tin, phục vụ cho nghề. B&ecirc;n cạnh đ&oacute;, đọc s&aacute;ch, lu&ocirc;n học hỏi, cũng như &yacute; thức r&egrave;n luyện thể chất để sức khỏe tốt, dẻo dai l&agrave; c&aacute;c kỹ năng sống cần thiết&quot;, nh&agrave; b&aacute;o cho biết.</p>\r\n', 5, 1, '1678355605_33.jpg'),
(6, 'Sách lý giải hiện tượng Murakami', '<p>Cuốn &quot;Haruki Murakami v&agrave; &acirc;m nhạc của ng&ocirc;n từ&quot; (t&aacute;c giả Jay Rubin) điểm lại tiểu sử, sự nghiệp của Murakami, ph&acirc;n t&iacute;ch b&uacute;t ph&aacute;p nghệ thuật của &ocirc;ng.</p>\r\n', '<p>Cuốn &quot;Haruki Murakami v&agrave; &acirc;m nhạc của ng&ocirc;n từ&quot; (t&aacute;c giả Jay Rubin) điểm lại tiểu sử, sự nghiệp của Murakami, ph&acirc;n t&iacute;ch b&uacute;t ph&aacute;p nghệ thuật của &ocirc;ng.</p>\r\n\r\n<p>S&aacute;ch do Y Khương dịch, gi&uacute;p độc giả trả lời c&acirc;u hỏi: V&igrave; sao nh&agrave; văn Nhật Bản n&agrave;y trở th&agrave;nh một hiện tượng văn chương to&agrave;n cầu? Điều g&igrave; khiến độc giả văn học tr&ecirc;n to&agrave;n thế giới đ&oacute;n chờ t&aacute;c phẩm mới của &ocirc;ng năm n&agrave;y qua năm kh&aacute;c, đặt cược giải Nobel văn chương cho c&aacute;i t&ecirc;n&nbsp;Haruki Murakami?</p>\r\n\r\n<p>Jay Rubin đưa người đọc trở lại những ng&agrave;y thơ ấu của cậu b&eacute; Murakami ở Kyoto, những năm &ocirc;ng học về nghệ thuật s&acirc;n khấu tại Đại học Waseda, Tokyo, giữa bối cảnh nước Nhật trải qua nhiều biến động. T&aacute;c giả đi s&acirc;u v&agrave;o qu&atilde;ng thời gian &ocirc;ng l&agrave;m chủ một c&acirc;u lạc bộ nhạc jazz ở qu&ecirc; nh&agrave;, vừa l&agrave;m bếp, chọn nhạc, vừa viết những cuốn s&aacute;ch đầu tay. T&aacute;c phẩm khắc họa ch&acirc;n dung Murakami quyết đo&aacute;n v&agrave; kỷ luật trong cuộc sống, việc s&aacute;ng t&aacute;c.</p>\r\n\r\n<p>&Ocirc;ng dịch s&aacute;ch, kết nối c&aacute;c nh&agrave; văn ở Mỹ v&agrave; phương T&acirc;y. &Ocirc;ng chạy marathon v&agrave; nghe jazz. &Ocirc;ng ngồi viết tiểu thuyết ở nhiều nơi, cả trong v&agrave; ngo&agrave;i nước Nhật. Haruki Murakami l&agrave; nh&agrave; văn ẩn m&igrave;nh trước c&ocirc;ng ch&uacute;ng, lu&ocirc;n tự duy tr&igrave; nguồn năng lượng s&aacute;ng tạo trong cuộc sống c&aacute; nh&acirc;n.</p>\r\n\r\n<p>Trong hầu hết t&aacute;c phẩm của Murakami, &acirc;m nhạc tạo ra sự kết nối giữa c&aacute;c phần. Jay Rubin viết: &quot;Với Murakami, &acirc;m nhạc l&agrave; phương tiện hữu hiệu nhất để tiến v&agrave;o những ng&aacute;ch kh&oacute;e s&acirc;u tận của v&ocirc; thức, c&aacute;i thế giới phi thời gian nằm b&ecirc;n trong t&acirc;m thức của ch&uacute;ng ta. Ở đ&oacute;, ở c&aacute;i l&otilde;i của tự ng&atilde;, tồn tại c&acirc;u chuyện về mỗi con người ch&uacute;ng ta: một tự sự đứt qu&atilde;ng m&agrave; ta chỉ c&oacute; thể biết được th&ocirc;ng qua h&igrave;nh ảnh. Giấc mơ l&agrave; một c&aacute;ch quan trọng để tiếp x&uacute;c với những h&igrave;nh ảnh ấy nhưng ch&uacute;ng thường trỗi l&ecirc;n v&ocirc; chừng khi ta đang thức, bị gi&aacute;n c&aacute;ch nhanh ch&oacute;ng bởi &yacute; thức, rồi bất ngờ quay trở lại nơi ch&uacute;ng xuất hiện&quot;.</p>\r\n\r\n<p>Bằng c&aacute;ch soi chiếu lại những t&aacute;c phẩm văn chương với đời sống c&aacute; nh&acirc;n của Murakami, Jay Rubin giải m&atilde; chi tiết những biểu tượng, ẩn dụ v&agrave; cấu tr&uacute;c độc đ&aacute;o trong thế giới tiểu thuyết của &ocirc;ng. Tờ&nbsp;<em>The Independent</em>&nbsp;nhận x&eacute;t: &quot;Th&agrave;nh c&ocirc;ng lớn của Rubin l&agrave; chứng minh được sự phức tạp của k&yacute; ức v&agrave; mặt nghĩa lặp đi lặp lại trong t&aacute;c phẩm của Murakami. Những lập luận sắc sảo của &ocirc;ng cho ta c&aacute;i nh&igrave;n s&acirc;u sắc để kh&aacute;m ph&aacute; ra t&iacute;nh li&ecirc;n tục tạo n&ecirc;n sự ng&ocirc;ng cuồng hơn người của nh&agrave; văn vĩ đại n&agrave;y. Tr&igrave;nh tự thời gian của một sự nghiệp lẫy lừng được Rubin sắp xếp rất th&uacute; vị v&agrave; mạch lạc, nhưng việc &ocirc;ng giỏi nhất ch&iacute;nh l&agrave; kết nối những h&igrave;nh ảnh phong ph&uacute; trong văn xu&ocirc;i của Murakami lại với nhau&quot;.</p>\r\n\r\n<p>Jay Rubin sinh năm 1941, l&agrave; dịch giả Mỹ, người c&oacute; c&ocirc;ng lớn trong việc đưa c&aacute;c t&aacute;c phẩm của Haruki Murakami đến với độc giả Anh ngữ. C&aacute;c t&aacute;c phẩm của Haruki Murakami như&nbsp;<em>Bi&ecirc;n ni&ecirc;n k&yacute; chim vặn d&acirc;y c&oacute;t</em>,&nbsp;<em>Rừng Na Uy</em>,&nbsp;<em>1Q84</em>,&nbsp;<em>Những c&acirc;u chuyện ngắn về người Nhật Bản...&nbsp;</em>do Jay Rubin dịch sang tiếng Anh c&oacute; sức l&ocirc;i cuốn lớn nhờ sự am hiểu văn h&oacute;a, con người t&aacute;c giả. Dịch phẩm&nbsp;<em>Bi&ecirc;n ni&ecirc;n k&yacute; chim vặn d&acirc;y c&oacute;t</em>&nbsp;của Jay Rubin đoạt Giải thưởng Noma của Nhật năm 2003. Hiện &ocirc;ng l&agrave; gi&aacute;o sư văn học Nhật danh dự tại Đại học Harvard.</p>\r\n', 5, 1, '1678355741_4.jpg'),
(7, 'Sách về kỹ năng trò chuyện với con ở tuổi dậy thì', '<p>T&aacute;c giả Lan Anh ra mắt s&aacute;ch &quot;B&iacute; quyết thương lượng cho tuổi dậy th&igrave;&quot;, b&agrave;n về c&aacute;ch bố mẹ gắn kết con c&aacute;i.&nbsp;</p>\r\n', '<p>T&aacute;c giả Lan Anh ra mắt s&aacute;ch &quot;B&iacute; quyết thương lượng cho tuổi dậy th&igrave;&quot;, b&agrave;n về c&aacute;ch bố mẹ gắn kết con c&aacute;i.</p>\r\n\r\n<p>C&acirc;y b&uacute;t cho biết nhiều phụ huynh than thở với chị rằng, đến tuổi dậy th&igrave;, con họ như biến th&agrave;nh người kh&aacute;c, tự x&acirc;y bức tường ngăn c&aacute;ch. T&aacute;c giả n&oacute;i: &quot;C&aacute;c con c&oacute; thể ứng xử chưa đ&uacute;ng, chưa tốt nhưng kh&ocirc;ng phải l&agrave; đứa trẻ hư hỏng. Ở tuổi dậy th&igrave;, kh&ocirc;ng phải l&uacute;c n&agrave;o h&agrave;nh động cũng thể hiện đ&uacute;ng bản chất&quot;.</p>\r\n\r\n<p>S&aacute;ch được đ&uacute;c r&uacute;t từ kinh nghiệm của t&aacute;c giả. Khi hai con ch&agrave;o đời, chị ghi lại qu&aacute; tr&igrave;nh chăm s&oacute;c để sau n&agrave;y chia sẻ với c&aacute;c b&agrave; mẹ. Theo Lan Anh, nhiều bố mẹ chưa quan t&acirc;m đ&uacute;ng mực đến thời kỳ dậy th&igrave; của c&aacute;c b&eacute; trai, cho rằng: &quot;Con trai ấy m&agrave;, kệ, ch&uacute;ng tộc ngộc rồi cũng sẽ tự th&agrave;nh đ&agrave;n &ocirc;ng cả th&ocirc;i...&quot;.</p>\r\n\r\n<p>T&aacute;c giả viết: &quot;C&aacute;c &ocirc;ng bố, b&agrave; mẹ n&ecirc;n t&igrave;m hiểu t&acirc;m tư của con, v&agrave; phải chạm v&agrave;o t&acirc;m tư đ&oacute; một c&aacute;ch tế nhị. C&aacute;c bậc phụ huynh n&ecirc;n đ&oacute;ng vai tr&ograve; người chia sẻ để vẽ đường cho &#39;những ch&uacute; hươu non&#39; hiểu r&otilde; kiến thức về gi&aacute;o dục giới t&iacute;nh&quot;. T&aacute;c giả n&oacute;i bố mẹ kh&ocirc;ng n&ecirc;n g&acirc;y &aacute;p lực bằng đ&ograve;n roi, h&igrave;nh thức kỷ luật..., m&agrave; cần c&oacute; niềm tin, biết lắng nghe, thấu hiểu con.</p>\r\n\r\n<p>Trong s&aacute;ch, t&aacute;c giả cũng đề cập c&aacute;ch bố mẹ gi&uacute;p con chạm ng&otilde; mối t&igrave;nh đầu nhưng kh&ocirc;ng vượt qu&aacute; giới hạn. Với th&ocirc;ng điệp &quot;dậy th&igrave; đừng giấu bố mẹ điều g&igrave;&quot;, t&aacute;c giả gửi gắm thanh thiếu ni&ecirc;n c&oacute; thể chủ động tr&ograve; chuyện với phụ huynh. Đồng thời, bố mẹ cần tinh tế quan s&aacute;t, gi&uacute;p con giải quyết những khủng hoảng t&acirc;m l&yacute;, tự chăm s&oacute;c tốt sức khỏe. &quot;Dậy th&igrave; l&agrave; khoảng thời gian của &#39;con diều&#39;, nghĩa l&agrave; c&aacute;c con d&ugrave; rất muốn &#39;bứt&#39; th&igrave; vẫn cần c&oacute; &#39;d&acirc;y&#39;. Bố mẹ phải thả d&acirc;y c&oacute; kiểm so&aacute;t, đ&uacute;ng khoảng c&aacute;ch, đ&uacute;ng l&uacute;c, nếu muốn cho con m&igrave;nh bay cao&quot;, t&aacute;c giả kết luận.</p>\r\n\r\n<p>L&ecirc; Lan Anh tốt nghiệp Đại học Sư phạm TP HCM, l&agrave;m trong lĩnh vực gi&aacute;o dục v&agrave; truyền th&ocirc;ng từ năm 1997. Chị từng ra mắt c&aacute;c s&aacute;ch:<em>&nbsp;Saigon zoom in, Con lớn từng ng&agrave;y, Tuổi 40 dấu y&ecirc;u</em>,&nbsp;<em>Vẫn y&ecirc;u</em>,&nbsp;<em>Ăn v&agrave; Y&ecirc;u v&agrave; Ăn v&agrave; Y&ecirc;u</em>,<em>&nbsp;Chỉ cần y&ecirc;u th&ocirc;i l&agrave; đủ...</em></p>\r\n', 5, 1, '1678355930_5.jpg'),
(8, 'Mùa hè bất tận - truyện tranh về tuổi học trò', '<p>Tinh thần t&iacute;ch cực, chống chọi ung thư trước khi qua đời của luật sư Mỹ Julie Yip-Williams được thể hiện trong hồi k&yacute; &quot;Th&aacute;o gỡ ph&eacute;p m&agrave;u&quot;.&nbsp;</p>\r\n', '<p>Họa sĩ L&acirc;m Ho&agrave;ng Tr&uacute;c kể c&acirc;u chuyện về lứa tuổi học sinh trung học cơ sở trong bộ truyện tranh mới.</p>\r\n\r\n<p>Ng&agrave;y 30/6,&nbsp;<em>M&ugrave;a h&egrave; bất tận&nbsp;</em>- t&aacute;c phẩm mới nhất của L&acirc;m Ho&agrave;ng Tr&uacute;c - ra mắt độc giả to&agrave;n quốc. Trước đ&oacute;, hơn 3.000 bản của bộ truyện đ&atilde; được người h&acirc;m mộ đặt mua trước.<strong>&nbsp;</strong>Bộ truyện cũng đ&aacute;nh dấu sự trở lại của nữ họa sĩ sau th&agrave;nh c&ocirc;ng của&nbsp;<em>Đường hoa</em>&nbsp;(2018).</p>\r\n\r\n<p><em>M&ugrave;a h&egrave; bất tận</em>&nbsp;kể về hai bạn trẻ c&ugrave;ng t&ecirc;n Phương, đang học năm cuối Trung học Cơ sở. Nội dung xoay quanh những c&acirc;u chuyện đời thường như trường lớp, thi cử, kỳ vọng của phụ huynh hay rung động đầu đời. T&aacute;c phẩm thuộc thể loại &quot;coming of age&quot;, về qu&aacute; tr&igrave;nh c&aacute;c nh&acirc;n vật chuyển m&igrave;nh từ một đứa trẻ th&agrave;nh người lớn. Tập truyện xo&aacute;y s&acirc;u những g&oacute;c s&acirc;u k&iacute;n của tuổi thiếu ni&ecirc;n, gi&atilde;i b&agrave;y nỗi l&ograve;ng của c&aacute;c bạn với gia đ&igrave;nh, nh&agrave; trường.</p>\r\n\r\n<p>L&acirc;m Ho&agrave;ng Tr&uacute;c lấy trải nghiệm bản th&acirc;n v&agrave; bạn b&egrave; xung quanh để ph&aacute;c n&ecirc;n ch&acirc;n dung hai nh&acirc;n vật ch&iacute;nh. Họa sĩ t&aacute;i hiện kh&ocirc;ng gian trường lớp v&agrave; gia đ&igrave;nh hết sức gần gũi của Việt Nam. Độc giả sẽ bắt gặp h&igrave;nh ảnh quen thuộc của c&acirc;y phượng đổ b&oacute;ng s&acirc;n trường, &aacute;o d&agrave;i trắng, con hẻm nhỏ nơi đ&aacute;m trẻ đi bộ đến lớp h&agrave;ng ng&agrave;y...</p>\r\n\r\n<p>T&aacute;c giả n&oacute;i về niềm cảm hứng th&ocirc;i th&uacute;c s&aacute;ng t&aacute;c bộ truyện: &quot;<em>M&ugrave;a h&egrave; bất tận</em>&nbsp;l&agrave; th&agrave;nh quả của nhiều năm quan s&aacute;t v&agrave; trải nghiệm cuộc sống của t&ocirc;i. T&ocirc;i kh&ocirc;ng đồng &yacute; với c&aacute;ch mọi người thường nh&igrave;n tuổi trẻ l&agrave; bồng bột, dại dột, mơ mộng. Rất nhiều bạn c&oacute; trải nghiệm phức tạp, cảm x&uacute;c s&acirc;u sắc m&agrave; kh&ocirc;ng được đ&aacute;nh gi&aacute; đ&uacute;ng. V&igrave; thế, t&ocirc;i muốn viết một t&aacute;c phẩm m&agrave; người đọc c&oacute; thể thấy rằng, c&aacute;i m&agrave; tuổi trẻ cần l&agrave; sự đồng cảm v&agrave; thấu hiểu&quot;.</p>\r\n\r\n<p>Tất cả trang vẽ được ph&aacute;c thảo bằng ch&igrave; rồi đi mực tr&ecirc;n khổ giấy A3, chủ yếu sử dụng c&aacute;c mảng đen trắng tương phản.<em>&nbsp;</em>T&aacute;c phẩm cũng đ&aacute;nh dấu sự chuyển đổi phong c&aacute;ch của L&acirc;m Ho&agrave;ng Tr&uacute;c.<em>&nbsp;</em>C&ocirc; kh&ocirc;ng c&ograve;n chịu ảnh hưởng nhiều từ manga Nhật Bản v&agrave; hạn chế những ph&acirc;n đoạn độc thoại nội t&acirc;m của nh&acirc;n vật như thời s&aacute;ng t&aacute;c<em>&nbsp;Đường hoa</em>.</p>\r\n\r\n<p>L&acirc;m Ho&agrave;ng Tr&uacute;c sinh năm 1991 tại Tiền Giang. C&ocirc; từng tốt nghiệp Đại học Kiến tr&uacute;c TP HCM trước khi chuyển hướng l&agrave;m t&aacute;c giả truyện tranh. Năm 2018, Ho&agrave;ng Tr&uacute;c ph&aacute;t h&agrave;nh tập truyện đầu ti&ecirc;n<em>&nbsp;Đường hoa&nbsp;</em>c&ugrave;ng Nh&agrave; xuất bản Kim Đồng, kể về một thanh ni&ecirc;n n&ocirc;ng th&ocirc;n với ước mơ th&agrave;nh họa sĩ.</p>\r\n', 5, 1, '1678356235_6.jpg'),
(9, 'Sách Ai đó chạy cùng ta Hành trình trưởng thành', '<p>Nh&agrave; văn Israel David Grossman viết về gi&aacute; trị của t&igrave;nh y&ecirc;u thuần khiết tr&ecirc;n h&agrave;nh tr&igrave;nh trưởng th&agrave;nh, trong &quot;Ai đ&oacute; chạy c&ugrave;ng ta&quot;.&nbsp;</p>\r\n', '<p>Nh&agrave; văn Israel David Grossman viết về gi&aacute; trị của t&igrave;nh y&ecirc;u thuần khiết tr&ecirc;n h&agrave;nh tr&igrave;nh trưởng th&agrave;nh, trong &quot;Ai đ&oacute; chạy c&ugrave;ng ta&quot;.</p>\r\n\r\n<p>Từng l&agrave; nh&agrave; b&aacute;o, tiểu thuyết gia David Grossman lấy hiện thực l&agrave;m nền m&oacute;ng cho c&acirc;u chuyện. Để khắc họa ch&acirc;n thực t&acirc;m l&yacute; nh&acirc;n vật trong&nbsp;<em>Ai đ&oacute; chạy c&ugrave;ng ta,</em>&nbsp;David đ&atilde; d&agrave;nh thời gian d&agrave;i t&igrave;m hiểu v&agrave; tiếp x&uacute;c với những thiếu ni&ecirc;n v&ocirc; gia cư tại Israel.</p>\r\n\r\n<p>S&aacute;ch<strong>&nbsp;</strong>xoay quanh cậu thiếu ni&ecirc;n 16 tuổi Assaf. Một ng&agrave;y nọ, Assaf v&ocirc; t&igrave;nh cuốn v&agrave;o h&agrave;nh tr&igrave;nh nguy hiểm v&agrave; kỳ quặc để t&igrave;m lại chủ của một con ch&oacute; đi lạc. Cậu gặp gỡ c&ocirc; g&aacute;i b&iacute; ẩn t&ecirc;n Tamar - người cũng đang trong cuộc phi&ecirc;u lưu của ri&ecirc;ng c&ocirc; - v&agrave; dần nảy sinh rung động.</p>\r\n\r\n<p>T&aacute;c phẩm kết hợp giữa thể loại trinh th&aacute;m v&agrave; t&igrave;nh cảm. David Grossman đ&atilde; đan c&agrave;i mảnh đời của hai con người b&igrave;nh thường c&ugrave;ng những sự kiện bất thường xảy đến với họ. &Ocirc;ng tập trung khắc họa t&igrave;nh y&ecirc;u thuần khiết đầu đời với n&eacute;t hồn nhi&ecirc;n, dễ thương. Từ đ&oacute;, truyền tải những b&agrave;i học v&agrave; &yacute; nghĩa về sự trưởng th&agrave;nh.</p>\r\n\r\n<p>Assaf d&agrave;nh kỳ nghỉ h&egrave; của m&igrave;nh để l&agrave;m c&ocirc;ng việc b&agrave;n giấy trong văn ph&ograve;ng T&ograve;a thị ch&iacute;nh. Cuộc sống của cậu y&ecirc;n b&igrave;nh do t&iacute;nh c&aacute;ch thụ động trước mọi vấn đề. Cậu kh&ocirc;ng đối diện v&agrave; giải quyết những kh&oacute; khăn.</p>\r\n\r\n<p>Đối lập Assaf hiền l&agrave;nh v&agrave; nhu nhược, Tamar nhanh nhẹn, sắc sảo, mạnh mẽ v&agrave; c&oacute; phần liều lĩnh. Mặc d&ugrave; vậy, c&ocirc; cũng gặp những vấn đề về gia đ&igrave;nh v&agrave; t&igrave;nh bạn. Khi Tamar lao v&agrave;o chốn nguy hiểm để gi&agrave;nh lại anh trai, hai người bạn th&acirc;n thiết nhất ng&oacute; lơ c&ocirc; để tham gia chuyến lưu diễn to&agrave;n quốc. Tamar nhận ra c&ocirc; đ&atilde; bị những người bạn l&acirc;u năm bỏ rơi.</p>\r\n\r\n<p>Cuối t&aacute;c phẩm, Tamar v&agrave; Assaf trưởng th&agrave;nh hơn, học được c&aacute;ch tho&aacute;t khỏi con người v&agrave; cuộc sống cũ, đ&oacute;n nhận bản ng&atilde; mới, h&ograve;a nhập với thế giới xung quanh.</p>\r\n\r\n<p>Khi h&aacute;t cho Assaf nghe, Tamar cảm thấy &quot;giọng c&ocirc; đ&atilde; thay đổi, một thay đổi rất nhỏ, như thể sự trong trẻo thơ trẻ trong đ&oacute; đ&atilde; ho&agrave;n to&agrave;n biến mất v&agrave; được thay thế bởi một thứ g&igrave; đ&oacute; mới mẻ lạ lẫm&quot;. Với c&ocirc;, &quot;n&oacute; gần như một trải nghiệm huyền b&iacute;, c&oacute; cảm gi&aacute;c như mọi sự vật sự việc trong vũ trụ n&agrave;y đều đang ở v&agrave;o đ&uacute;ng vị tr&iacute; của m&igrave;nh&quot;. Đối với Assaf, cậu thấy &quot;to&agrave;n bộ cuộc đời cậu trước thời khắc n&agrave;y chỉ l&agrave; một m&agrave;n giới thiệu, một sự khởi động; rằng cuối c&ugrave;ng th&igrave; cậu cũng đang bắt đầu sống&quot;.</p>\r\n\r\n<p>Qua c&acirc;u chuyện của hai nh&acirc;n vật ch&iacute;nh, t&aacute;c phẩm c&ograve;n phản &aacute;nh thực trạng x&atilde; hội Israel với những mặt tối, n&ecirc;u bật gi&aacute; trị của t&igrave;nh y&ecirc;u tr&ecirc;n nền hiện thực: t&igrave;nh y&ecirc;u đ&ocirc;i lứa, t&igrave;nh cảm gia đ&igrave;nh hay giữa con người v&agrave; vật nu&ocirc;i. T&aacute;c giả cũng mi&ecirc;u tả v&agrave; khắc họa những n&eacute;t đẹp của cảnh vật, văn h&oacute;a, con người Israel.</p>\r\n\r\n<p>Trong sự kiện ra mắt s&aacute;ch tại H&agrave; Nội ng&agrave;y 23/11, nh&agrave; văn Hiền Trang, bi&ecirc;n tập vi&ecirc;n Trần Linh v&agrave; Đức Anh n&oacute;i về những gi&aacute; trị, li&ecirc;n hệ của bản th&acirc;n với cuốn s&aacute;ch. Theo c&aacute;c diễn giả, t&aacute;c phẩm mang tới nhiều b&agrave;i học nh&acirc;n văn. Thay v&igrave; đi theo hướng đen tối v&agrave; sử thi, t&aacute;c giả kể một c&acirc;u chuyện lạc quan, đời thường. Độc giả từ nhiều độ tuổi đều c&oacute; thể đồng cảm với một số nh&acirc;n vật, chi tiết. S&aacute;ch cũng khắc họa r&otilde; n&eacute;t vẻ đẹp của nghệ thuật v&agrave; &acirc;m nhạc.</p>\r\n\r\n<p>David Grossman (sinh năm 1954) l&agrave; nh&agrave; văn người Israel. C&aacute;c t&aacute;c phẩm của &ocirc;ng được dịch ra hơn 30 thứ tiếng v&agrave; gi&agrave;nh nhiều giải thưởng. Cuốn&nbsp;<em>Con ngựa bước v&agrave;o qu&aacute;n bar</em>&nbsp;mang lại giải Man Booker Quốc tế 2017 cho David. Năm 2018, &ocirc;ng được trao tặng Giải thưởng Israel - giải thưởng văn h&oacute;a cấp nh&agrave; nước - sau khi từng từ chối nhận giải năm 2015 v&igrave; l&yacute; do ch&iacute;nh trị.</p>\r\n', 5, 1, '1678356388_7.jpg'),
(10, 'Hồi ký Harry: Vòng xoáy hỗn loạn về cuộc sống hoàng gia', '<p>Trong hồi k&yacute; &quot;Spare&quot;, Harry n&oacute;i việc l&agrave;m ho&agrave;ng tử khiến anh trở th&agrave;nh kẻ thất nghiệp.&nbsp;</p>\r\n', '<p>Trong hồi k&yacute; &quot;Spare&quot;, Harry n&oacute;i việc l&agrave;m ho&agrave;ng tử khiến anh trở th&agrave;nh kẻ thất nghiệp.</p>\r\n\r\n<p>Theo th&ocirc;ng tin từ nh&agrave; xuất bản Penguin Random House,&nbsp;<em>Spare</em>&nbsp;b&aacute;n được 1,43 triệu bản với tất cả định dạng ở Mỹ, Canada v&agrave; Anh sau một ng&agrave;y ra mắt (10/1),&nbsp;<a href=\"https://vnexpress.net/hoi-ky-cua-harry-pha-ky-luc-ngay-dau-phat-hanh-tai-anh-4558681.html?fbclid=IwAR0T4ghj69qMzFZObkWvq6jmIB9oxozkWN75zeilH29__VvQKHHLKNhHCkA\">ph&aacute; kỷ lục</a>&nbsp;ở Anh với 400.000 cuốn. Shannon DeVito, gi&aacute;m đốc hệ thống ph&acirc;n phối s&aacute;ch Barnes &amp; Noble ở Mỹ, n&oacute;i với&nbsp;<em>NY Times</em>: &quot;Một số người đến mua ngay trước giờ l&agrave;m việc, số kh&aacute;c đến v&agrave;o giờ nghỉ trưa, c&ograve;n lại đến sau giờ l&agrave;m. Nhưng tốc độ b&aacute;n h&agrave;ng trong ng&agrave;y l&agrave; rất nhanh&quot;.</p>\r\n\r\n<p>Tờ&nbsp;<em>Vox&nbsp;</em>nhận x&eacute;t<em>&nbsp;</em>hồi k&yacute; của Ho&agrave;ng tử Harry l&agrave; một t&aacute;c phẩm m&acirc;u thuẫn. Cuốn s&aacute;ch giống như lời c&ocirc;ng k&iacute;ch từ một c&aacute; nh&acirc;n đầy bất m&atilde;n, kh&ocirc;ng biết c&aacute;ch l&agrave;m thế n&agrave;o để điều chỉnh cảm x&uacute;c của bản th&acirc;n, nhưng cũng hấp dẫn một c&aacute;ch kỳ lạ, khiến người đọc tự hỏi liệu n&oacute; c&oacute; n&ecirc;n được c&ocirc;ng khai hay kh&ocirc;ng.</p>\r\n\r\n<p>400 trang của<em>&nbsp;Spare</em>&nbsp;l&agrave; một v&ograve;ng xo&aacute;y hỗn loạn về cuộc sống ho&agrave;ng gia. N&oacute; xoay quanh c&aacute;i chết của mẹ Harry, C&ocirc;ng nương Diana v&agrave;o năm 1997; thời ni&ecirc;n thiếu của ho&agrave;ng tử khi c&ograve;n l&agrave; cậu b&eacute; c&ograve;i cọc; những ng&agrave;y anh phục vụ trong qu&acirc;n ngũ; cuộc h&ocirc;n nh&acirc;n với Meghan Markle v&agrave; thời điểm anh quyết định rời cương vị th&agrave;nh vi&ecirc;n cao cấp của Ho&agrave;ng gia Anh năm 2020.</p>\r\n\r\n<p>J.R. Moehringer, t&aacute;c giả từng đoạt giải Pulitzer, người chấp b&uacute;t cho ho&agrave;ng tử, đ&atilde; kể lại mọi chuyện với giọng điệu trong s&aacute;ng v&agrave; v&ocirc; tư. Những chi tiết nhỏ nhặt về việc c&aacute;c qu&yacute; tộc tranh gi&agrave;nh chỗ đỗ xe ở Cung điện Kensington, m&oacute;n qu&agrave; Gi&aacute;ng sinh keo kiệt từ C&ocirc;ng ch&uacute;a Margaret... được kể sinh động. Qua ng&ograve;i b&uacute;t của Moehringer, Harry hiện l&ecirc;n l&agrave; kiểu người vui t&iacute;nh, dễ mến, thẳng thắn, &iacute;t quan t&acirc;m đến văn chương. C&aacute;c c&acirc;u n&oacute;i của anh đơn giản, ngắn. Trong s&aacute;ch, anh kể cho&aacute;ng ngợp bởi sự tinh tế trong văn học của Meghan khi c&ocirc; ấy đề cập đến&nbsp;<em>Eat Pray Love,</em>&nbsp;một cuốn s&aacute;ch m&agrave; Harry chưa từng nghe đến.</p>\r\n\r\n<p>Trong k&yacute; ức tuổi thơ của ho&agrave;ng tử, người đọc cảm nhận được t&igrave;nh cảm anh d&agrave;nh cho cha v&agrave; anh trai. Đ&oacute; từng l&agrave; những người anh th&acirc;n thiết nhất tr&ecirc;n đời, nhưng giờ đ&acirc;y mối quan hệ của họ kh&ocirc;ng thể h&ograve;a giải. Charles xuất hiện với h&igrave;nh ảnh người cha ngọt ng&agrave;o, hay qu&ecirc;n, thường để lại những lời nhắn tr&ecirc;n gối Harry về việc &ocirc;ng tự h&agrave;o thế n&agrave;o về anh. Anh trai William l&agrave; người duy nhất hiểu nỗi đau của Harry sau khi mẹ qua đời, l&agrave; người cảm th&ocirc;ng với em trai về việc lớn l&ecirc;n trong ho&agrave;ng gia, giữa những &aacute;nh đ&egrave;n flash của c&aacute;c tay săn ảnh, v&agrave; l&agrave; người duy nhất anh t&acirc;m sự mỗi khi gặp vấn đề.</p>\r\n\r\n<p><strong>Tuy nhi&ecirc;n, theo Harry, cả Charles v&agrave; William đều bị tha h&oacute;a bởi sức mạnh của vương miện, điều n&agrave;y khiến họ t&igrave;m đủ c&aacute;ch để đ&aacute;nh b&oacute;ng bản th&acirc;n, coi anh l&agrave; vật hy sinh</strong>.&nbsp;Những người thừa kế, lu&ocirc;n lu&ocirc;n, ở tr&ecirc;n kẻ dự bị.</p>\r\n\r\n<p>&quot;T&ocirc;i được sinh ra trong trường hợp c&oacute; điều g&igrave; đ&oacute; xảy ra với Willy, t&ocirc;i sẽ được gọi để hỗ trợ, đ&aacute;nh lạc hướng, v&agrave; nếu cần, l&agrave; hiến tạng. C&oacute; thể l&agrave; thận. Truyền m&aacute;u. Hoặc tủy&quot;, Harry viết. Thực tế, William kh&ocirc;ng cần hiến tạng, nhưng Harry cho rằng cả anh trai v&agrave; bố lu&ocirc;n c&oacute; thể sử dụng thứ g&igrave; đ&oacute; để giảm bớt &aacute;p lực từ sự ch&uacute; &yacute; của b&aacute;o ch&iacute;, v&agrave; đ&oacute; ch&iacute;nh l&agrave; Harry.</p>\r\n\r\n<p>Ho&agrave;ng tử n&oacute;i mối quan hệ của họ kh&oacute; cứu v&atilde;n khi anh kết h&ocirc;n với Meghan. William can thiệp ng&agrave;y một nhiều v&agrave;o chuyện ri&ecirc;ng tư của em trai. Đỉnh điểm, trong một lần tranh c&atilde;i, William quật Harry ng&atilde; xuống s&agrave;n mạnh đến nỗi đĩa thức ăn cho ch&oacute; vỡ vụn b&ecirc;n dưới. Khi William y&ecirc;u cầu Harry đ&aacute;nh lại như l&uacute;c họ c&ograve;n nhỏ, anh từ chối.</p>\r\n\r\n<p>Harry từng sốc khi bố n&oacute;i kh&ocirc;ng thể hỗ trợ t&agrave;i ch&iacute;nh cho anh, trong khi việc trở th&agrave;nh ho&agrave;ng tử khiến anh v&agrave; William thất nghiệp. Anh cũng nhắc lại việc Meghan bị c&aacute;c tờ b&aacute;o l&aacute; cải quấy nhiễu đến nỗi từng định tử tự.</p>\r\n\r\n<p>Nh&agrave; b&aacute;o Constance Grady nhận x&eacute;t: &quot;Với những bi kịch trong&nbsp;<em>Spare</em>&nbsp;v&agrave; tất cả những g&igrave; Harry kể, r&otilde; r&agrave;ng rằng Charles v&agrave; William sẽ coi cuốn hồi k&yacute; n&agrave;y kh&ocirc;ng phải như một lời giải th&iacute;ch, một bức thư tự t&igrave;nh m&agrave; l&agrave; một sự phản bội tồi tệ. Ngay cả khi chưa bao giờ đọc n&oacute;, l&agrave;m sao họ c&oacute; thể tr&aacute;nh được d&ograve;ng tin tức v&ocirc; tận, những cuộc phỏng vấn m&agrave; Harry thực hiện để giới thiệu về cuốn s&aacute;ch, bộ phim t&agrave;i liệu Netflix, d&ograve;ng th&ocirc;ng tin kh&ocirc;ng ngừng nghỉ về Harry v&agrave; Meghan m&agrave; họ đ&atilde; tung ra với cả thế giới?... C&oacute; vẻ như Harry, người gh&eacute;t b&aacute;o ch&iacute; v&igrave; việc n&oacute; thường xuy&ecirc;n x&acirc;m phạm quyền ri&ecirc;ng tư của anh, đ&atilde; tự m&igrave;nh trở th&agrave;nh nh&agrave; b&aacute;o để gi&agrave;nh lấy sự đồng cảm...&quot;.</p>\r\n', 5, 1, '1678356554_8.jpg');

