) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

--
-- Đang đổ dữ liệu cho bảng `tbl_sanpham`
--

INSERT INTO `tbl_sanpham` (`id_sanpham`, `masp`, `tensanpham`, `giasp`, `soluong`, `hinhanh`, `tomtat`, `noidung`, `tinhtrang`, `id_danhmucsp`, `chatlieu`, `kichthuoc`, `trongluong`) VALUES
(21, 'V001', 'Tôi Thấy Hoa Vàng Trên Cỏ Xanh (Bản In Mới - 2018)', '96250', 25, '1677725083_vanhoc4.jpg', '', 'Những câu chuyện nhỏ xảy ra ở một ngôi làng nhỏ: chuyện người, chuyện cóc, chuyện ma, chuyện công chúa và hoàng tử , rồi chuyện đói ăn, cháy nhà, lụt lội,... Bối cảnh là trường học, nhà trong xóm, bãi tha ma. Dẫn chuyện là cậu bé 15 tuổi tên Thiều. Thiều có chú ruột là chú Đàn, có bạn thân là cô bé Mận. Nhưng nhân vật đáng yêu nhất lại là Tường, em trai Thiều, một cậu bé học không giỏi. Thiều, Tường và những đứa trẻ sống trong cùng một làng, học cùng một trường, có biết bao chuyện chung. Chúng nô đùa, cãi cọ rồi yêu thương nhau, cùng lớn lên theo năm tháng, trải qua bao sự kiện biến cố của cuộc đời.\r\nTác giả vẫn giữ cách kể chuyện bằng chính giọng trong sáng hồn nhiên của trẻ con. 81 chương ngắn là 81 câu chuyện hấp dẫn với nhiều chi tiết thú vị, cảm động, có những tình tiết bất ngờ, từ đó lộ rõ tính cách người. Cuốn sách, vì thế, có sức ám ảnh.', 1, 29, 'NXB Trẻ', 'Trẻ', 'Nguyễn Nhật Ánh'),
(23, 'V002', 'Chiến Binh Cầu Vồng (Tái Bản 2020)', '83930', 35, '1677725068_vanhoc1.jpg', '', 'Trong ngày khai giảng, nhờ sự xuất hiện vào phút chót của cậu bé thiểu năng trí tuệ Harun, trường Muhammadiyah may mắn thoát khỏi nguy cơ đóng cửa. Nhưng ước mơ dạy và học trong ngôi trường Hồi giáo ấy liệu sẽ đi về đâu, khi ngôi trường xập xệ dường như sẵn sàng sụp xuống bất cứ lúc nào, khi lời đe dọa đóng cửa từ viên thanh tra giáo dục luôn lơ lửng trên đầu, khi những cỗ máy xúc hung dữ đang chực chờ xới tung ngôi trường để dò mạch thiếc…?\r\nChiến binh Cầu vồng có cả tình yêu trong sáng tuổi học trò lẫn những trò đùa tinh quái, cả nước mắt lẫn tiếng cười – một bức tranh chân thực về hố sâu ngăn cách giàu nghèo, một tác phẩm văn học cảm động truyền tải sâu sắc nhất ý nghĩa đích thực của việc làm thầy, việc làm trò và việc học.', 1, 29, 'Nhã Nam', 'NXB Hội Nhà Văn', 'Andrea Hirata'),
(24, 'V003', 'Totto-Chan Bên Cửa Sổ (Tái Bản 2019)', '75460', 20, '1677725054_vanhoc2.jpg', '', 'Totto-chan bên cửa sổ là cuốn sách gối đầu giường của nhiều thế hệ trẻ em trên toàn thế giới suốt ba mươi năm nay. Sau khi xuất bản lần đầu vào năm 1981, cuốn sách đã gây được tiếng vang lớn không chỉ ở Nhật Bản mà còn trên toàn thế giới. Tính đến năm 2001, tổng số bản sách bán ra ở Nhật đã lên đến 9,3 triệu bản, trở thành một trong những cuốn sách bán chạy nhất trong lịch sử ngành xuất bản nước này. Cuốn sách đã được dịch ra 33 thứ tiếng khác nhau, như Anh, Pháp, Đức, Hàn Quốc, Trung Quốc…Khi bản tiếng Anh của Totto-chan được xuất bản tại Mỹ, tờ New York Times đã đăng liền hai bài giới thiệu trọn trang, một “vinh dự” hầu như không tác phẩm nào có được.', 1, 29, 'Nhã Nam', 'NXB Hội Nhà Văn', 'Kuroyanagi Tetsuko'),
(25, 'V004', 'Nhà Giả Kim (Tái Bản 2020)', '55300', 32, '1677725039_vanhoc3.jpg', '', 'Tất cả những trải nghiệm trong chuyến phiêu du theo đuổi vận mệnh của mình đã giúp Santiago thấu hiểu được ý nghĩa sâu xa nhất của hạnh phúc, hòa hợp với vũ trụ và con người.\r\nTiểu thuyết Nhà giả kim của Paulo Coelho như một câu chuyện cổ tích giản dị, nhân ái, giàu chất thơ, thấm đẫm những minh triết huyền bí của phương Đông. Trong lần xuất bản đầu tiên tại Brazil vào năm 1988, sách chỉ bán được 900 bản. Nhưng, với số phận đặc biệt của cuốn sách dành cho toàn nhân loại, vượt ra ngoài biên giới quốc gia, Nhà giả kim đã làm rung động hàng triệu tâm hồn, trở thành một trong những cuốn sách bán chạy nhất mọi thời đại, và có thể làm thay đổi cuộc đời người đọc.', 1, 29, 'Nhã Nam', 'NXB Hội Nhà Văn', 'Paulo Coelho'),
(26, 'V005', ' Hai Mặt Của Gia Đình', '78000', 30, '1677725024_vanhoc5.jpg', '', 'Vì sao chúng ta luôn bộc phát cảm xúc khi đứng trước gia đình mình. Vì sao nỗi đau của gia đình nơi bạn sinh ra và lớn lên lại lặp lại trong gia đình hiện tại? Nhiều khi những tổn thương đến từ gia đình nơi chúng ta sinh ra và lớn lên lại chính là nguyên nhân của các vấn đề trong gia đình hiện tại.\r\n\r\nGia đình trở thành nơi tiếp thêm sức mạnh và cũng là nơi khiến đôi vai chúng ta thêm nặng trĩu, là nơi ẩn náu của những mâu thuẫn tinh vi đằng sau những phút giây thân mật, là nơi tràn đầy thương yêu nhưng có cả sự ghét bỏ. Gia đình lúc nào cũng có hai mặt như vậy.\r\n\r\nChoi Kwang Huyn – một nhà tâm lý học hàng đầu Hàn Quốc, người đã tham vấn tâm lý cho rất nhiều trường hợp có khúc mắc về tổn thương tâm lý xuất phát từ gia đình sẽ chỉ ra nguyên nhân của những tổn thương, những nỗi đau mà chúng ta phải chịu từ gia đình mình, đồng thời đưa ra cách giải quyết đối với các vấn đề gia đình nhức nhối hiện tại.\r\n\r\nCuốn sách cho ta cái nhìn mới về mối quan hệ gia đình. Qua những câu chuyện của các gia đình khác nhau, người đọc lần lượt sẽ thấy được sự tương đồng và có thêm hiểu biết về những khái niệm như: ngoại tình, chứng nghiện một điều gì đó, lựa chọn người bạn đời tương đồng, chuyển di nỗi đau…từ đó tìm được cách chấp nhận và xoa dịu những tổn thương trong lòng mỗi người.', 1, 29, 'AZ Việt Nam', 'NXB Thanh Niên', 'Choi Kwanghuyn'),
(27, 'V006', 'Phẩm Cách Của Lời Nói', '43000', 10, '1677725007_vanhoc6l.jpg', '', 'Có lẽ mỗi cuốn sách là một khu rừng được tạo nên bởi hàng trăm ngàn con chữ. Tôi mong bạn đừng chạy một mạch băng qua khu từng mang tên Phẩm cách của lời nói này. Bạn hãy từ từ dạo bước như đang tản bộ trong công viên yên tĩnh vào sớm mai.\r\nĐọc một cuốn sách không phải là hành động đọc suy nghĩ và tâm hồn của tác giả mà rốt cục chính là đọc “bản thân“. Hi vọng mỗi khi lật mở một trang trong cuốn Phẩm cách của lời nói này, bạn đọc sẽ không ngừng tự đặt câu hỏi về lời nói cũng như thế giới quan của bản thân.\r\nSau khi gấp cuốn sách lại, tôi mong thỉnh thoảng bạn đừng dùng đôi môi mà hãy dùng đôi tai để chiếm được trái tim người đối diện. Và tôi cũng mong rằng sự chân thành sâu thẳm trong trái tim bạn có thể xoa dịu những đớn đau trong lòng người ấy.\r\nTôi mong rằng bạn hãy tiến gần thêm chút nữa về phía người quý giá bên cạnh bạn lúc này. Tôi thành tâm nguyện cầu điều đó.', 1, 29, 'AZ Việt Nam', 'NXB Phụ Nữ Việt Nam', 'Ki Ju Lee'),
(28, 'V007', 'Drama Nuôi Tôi Lớn Loài Người Dạy Tôi Khôn', '76230', 24, '1677724986_vanhoc7.jpg', '', 'Đến với DRAMA NUÔI TÔI LỚN, LOÀI NGƯỜI DẠY TÔI KHÔN – cái tên nói lên tất cả. Ghi dấu ấn mạnh mẽ nhờ giọng văn hài hước, hóm hỉnh, trẻ trung và đặc biệt là biệt tài gây cười đặc trưng có một không hai, chắc chắn với cuốn sách đầu tay này, Pương Pương sẽ chinh phục được các độc giả khó tính nhất.\r\nKhông đi theo lối đi cũ mòn của văn chương hoa mĩ. Dưới góc nhìn của một cô tác giả trẻ trong thế hệ 2k, với lượng người follow cực đông đảo trên Tiktok lẫn Fanpage, Pương tạo nên một phong cách văn chương có một không hai. Vừa gây cười, vừa cuốn hút mà vẫn đầy ý nghĩa về gia đình và cuộc sống.\r\nĐó có thể chỉ là những câu chuyện về gia đình, chuyện xã hội, đến những chuyện bóc phốt đánh ghen hay là những cú lừa trên mạnh xã hội đầy thị phi ngang dọc. Tất cả hội tụ tạo nên một cuốn sách siêu đặc biệt mà nhát định bạn cần phải có trong xã hội đầy Drama này.', 1, 29, 'AZ Việt Nam', 'NXB Phụ Nữ Việt Nam', 'Pương Pương'),
(29, 'V008', 'Tạm Biệt Tôi Của Nhiều Năm Về Trước', '73150', 32, '1677724970_vanhoc8.jpg', '', 'Dành tặng bạn, những người muốn buông bỏ những “điều đã cũ” nhưng chưa đủ can đảm.\r\nDành tặng những ai đang khao khát được “chạm vào”, được vỗ về và thấu hiểu.\r\n\r\nDành tặng cho tất cả chúng ta, trong những năm tháng tươi đẹp nhất của thanh xuân, đang có một chốn để mơ về, có một ước mơ để theo đuổi và có một ai đó để da diết nhớ thương.\r\n“Tạm biệt tôi của nhiều năm về trước” là ấn phẩm mới nhất của An, chàng tác giả từng được biết tới với “cơn sốt” sách “Hẹn nhau phía sau tan vỡ” - liên tục cháy hàng và chinh phục trái tim của hàng vạn độc giả trẻ.\r\nTrong “Tạm biệt tôi của nhiều năm về trước”, với 248 trang viết kèm minh họa được thực hiện bởi chính tác giả, An dẫn bạn đọc bóc tách từng tổn thương chưa được chữa lành bên trong mình. Dù đó là vết thương do ai gây ra, vì lý do gì, vào thời điểm nào, còn sâu hay đã vơi… thì đều được vỗ về và ủi an.\r\n“Để trưởng thành chúng ta phải nói lời tạm biệt với nhiều người, và có thể là tạm biệt chính bản thân mình trong quá khứ.”\r\n“Đôi khi chúng ta tha thứ cho một người không phải vì họ xứng đáng nhận được, mà vì bạn xứng đáng với sự thanh thản, không cần bận tâm về những chuyện đã qua nữa.”\r\n“Chỉ khi bạn đi về phía ánh sáng, chiếc bóng mới hắt về phía sau. Đừng tự giam cầm mình trong quá khứ, hãy làm gì đó để bản thân cảm thấy tốt hơn. Rồi mọi chuyện cũng sẽ qua, mọi vết thương đều sẽ lành lại.“\r\nHãy chọn nói Tạm Biệt những kỉ niệm đã cũ mèm, cho phép nó rời bỏ mình, để ngày thêm an tĩnh và đêm thêm an yên.', 1, 29, 'Skybooks', 'Dân Trí', 'An'),
(30, 'V009', 'Gatsby Vĩ Đại', '103950', 28, '1677724957_vanhoc9.jpg', '', 'Kiệt tác Gatsby vĩ đại (1925) của văn hào Mỹ F. Scott Fitzgerald (1896-1940) là câu chuyện về chàng trai Jay Gatsby muốn thoát khỏi thân phận nghèo hèn và đặt chân vào tầng lớp cao sang mà hiện thân là một cô gái nhà giầu anh đã yêu và được yêu khi còn khoác trên vai bộ quân phục không phân biệt đẳng cấp giầu nghèo. Qua mối tình mãnh liệt và mê muội của Gatsby, bằng một lối văn cực kỳ súc tích, đa tầng khiến cho ngay T. S. Eliot, nhà văn gốc Mỹ đương thời với Fitzgerald, Giải thưởng Nobel về văn học năm 1948, như kể lại trong thư viết cho tác giả, đã phải đọc đi đọc lại ba lần trong năm 1925, Fitzgerald đã vẽ ra một trong bức tranh cô đọng nhất, sâu sắc và giầu biểu tượng nhất về xã hội Mỹ trong những năm 1920 với đủ các mặt tàn nhẫn, giả dối, bịp bợm và ích kỷ của nó.\r\n\r\nMang trong mình những ước vọng và cả những mâu thuẫn chứa chất trong tâm hồn con người Mỹ, trong kiệt tác văn chương này tác giả đã phơi bày một cách ngậm ngùi, đau xót sự tan vỡ của những mộng tưởng ở nước Mỹ, mặt giả dối, hư trá của Giấc mơ Mỹ từng được nhen lên và nuôi dưỡng ở những người định cư đầu tiên kể từ giây phút khám phá và đặt chân lên “Tân thế giới” này.\r\nKhông được độc giả đương thời nồng nhiệt đón nhận ngay mà phải đợi đến 20 năm sau, khi Fitzgerald đã qua đời 5 năm, Gatsby vĩ đại mới được công chúng Mỹ cũng như các nước trên thế giới hoan nghênh như một tác phẩm kinh điển của văn học Mỹ, được chọn là một trong “10 tác phẩm lớn nhất của mọi thời đại” do tạp chí Time  tổ chức, đứng thứ hai trong danh sách 100 tiểu thuyết hay nhất thế kỷ 20 của Modern Library, được đưa vào giảng dạy tại các trường trung học và đại học ở Mỹ và nhiều nước trên thế giới', 1, 29, 'Không có', 'Thế Giới', 'F Scott Fitzgerald'),
(31, 'V010', 'Không Gia Đình (Tái Bản)', '110880', 15, '1677724941_vanhoc10.jpg', '', 'Thở xa lắm, giữa lòng nước Pháp thế kỷ XIX, có một câu chuyện...\r\nCâu chuyện về cậu bé bất hạnh Rémi lang bạt trên dặm trường thiên lý, dấn thân giữa tất cả những bần cùng đói khổ và những xa hoa lộng lẫy. Cậu thiếu niên nhỏ tuổi đã đi qua biết bao miền quê, thấy biết bao cảnh đời, mỗi bước chân đều in dấu ấn của những câu chuyện kỳ lạ, có lúc hoan hỉ mừng vui, có khi thê lương đau đớn nhưng luôn lấp lánh tình người. Cuộc hành trình của Rémi với đoàn xiếc khỉ, chó, với những người thợ mỏ, với cậu bé hát rong người Ý đưa người đọc trải nghiệm mọi cung bậc cảm xúc: thích thú, bất ngờ, hồi hộp, thương tâm, thậm chí cả tuyệt vọng và dạy cho ta - những người chưa, đang, hay đã trưởng thành - những bài học thấm thía về ý chí, nghị lực và lao động chân chính...\r\nBàn về Không gia đình không cần bất cứ lời bình luận hoa mỹ nào khác, chỉ gói gọn trong hai từ: Kinh điển!', 1, 29, 'Định Tị', 'NXB Văn Học', 'Hector Malot'),
(32, 'V011', 'Thời Thơ Ấu (Tái Bản 2021)', '72000', 40, '1677724927_vanhoc11.jpg', '', 'Là những trang hồi kí của cậu bé Alêchxây về một quá khứ đau buồn và đầy sóng gió trong gia đình, bắt đầu với cái chết đầy ám ảnh của người cha mà cậu vô cùng kính yêu.\r\n\r\nSau biến cố lớn lao ấy, cậu về quê với bà và mẹ, một miền quê mà cậu phải chứng kiến nhiều cảnh cãi vã, nhiều trận đòn và nhiều cái chết.\r\n\r\nSau khi chuyển nhà, cậu bước vào đời qua những trang sách, những câu chuyện, những việc làm đầy nhân văn của bà ngoại cùng cách dạy dỗ vô cùng nghiêm khắc của người ông.\r\n\r\nCậu bé đã kiên cường vượt qua những khó khăn cùng khát vọng học tập ngày càng mãnh liệ', 1, 29, 'Cty Văn Hóa & Truyền Thông Trí Việt.', 'NXB Văn Học', 'Macxim Gorki'),
(33, 'V012', 'Phía Tây Không Có Gì Lạ (Tái Bản 2022)', '64000', 28, '1677724912_vanhoc12.jpg', '', 'Thế chiến thứ nhất nổ ra, những chàng trai đang ngồi trên ghế nhà trường bị chuyển thẳng ra mặt trận. Tại đây sự khốc liệt của chiến tranh đã khiến họ tê dại khi bom đạn không chỉ tước đi những phần cơ thể mà còn cả tâm hồn. Thế nên chưa kịp trưởng thành họ đã trở nên già nua, bởi gần với cái chết hơn là sự sống. Họ cũng chẳng còn tin tưởng ai, chẳng thiết tha điều gì, kể cả ngày trở về.\r\nCho nên khi tất cả đồng đội cùng trang lứa đã ngã xuống, cái chết đối với những chàng trai ấy là sự giải thoát. Họ nằm xuống nhẹ nhangfm thanh than đến độ tưởng như chẳng hề may may lay động đến thứ gì xung quanh, dù chỉ là một ngọn cỏ. Mặt trận hoàn toàn yên tĩnh, bản báo cáo chiến trường chỉ ghi vẻn vẹn một câu: “Ở phía Tây, không có gì lạ.” Phải, chẳng có gì lạ, chỉ có một người vừa rời khỏi cuộc đời khi độ tuổi mới chớm đôi mươi.', 1, 29, 'Đông A', 'NXB Văn Học', 'Erich Maria Remarque'),
(36, 'V013', 'Vì Con Gái Tôi Có Thể Đánh Bại Cả Ma Vương - Tập 1', '95000', 34, '1677724898_vanhoc13.jpg', '', 'Tại thế giới này, tất cả mọi ‘người’ đều nói rằng cầu vồng được tạo nên từ bảy sắc màu. Bởi vì cầu vồng chính là hiện thân của những đấng tối cao kiểm soát thế giới. Cầu vồng xuất hiện trên bầu trời khi những vị thần cùng quan sát mặt đất.\r\nNơi ấy… chính là điểm bắt đầu cho một câu chuyện.\r\nDale Reki – một mạo hiểm giả trẻ tuổi với những kỹ năng thượng thừa cùng những thành tựu vượt trội của riêng mình. Trong một lần thực hiện nhiệm vụ tại rừng sâu, cậu đã gặp Latina - một cô nhóc thuộc Quỷ Nhân tộc. Tuy nhiên, cô bé lại bị khuyết một bên sừng - đó là dấu hiệu tội phạm của Quỷ Nhân Tộc. Thương cảm cho số phận của cô nhóc, cậu đã quyết định trở thành một người “Bảo Hộ” cho em.\r\nLiệu rằng Dale Reki – một mạo hiểm giả trẻ tuổi hàng đầu có thể trở thành người cha hạng nhất???\r\nBằng giọng văn đáng yêu từng làm tan chảy hàng triệu trái tim Nhật Bản, series light novel mới nhất của Sky Light Novel “Vì con gái tôi có thể đánh bại cả ma vương” của CHIROLU sẽ đưa bạn vào một thế giới diệu kỳ, cùng câu chuyện không-thể-ngọt-ngào-hơn của “hai cha con” nhà Dale và Latina!', 1, 29, 'Skybooks', 'NXB Phụ Nữ Việt Nam', 'CHIROLU'),
(37, 'V014', 'Thiên Sứ Nhà Bên - Tập 1', '95000', 16, '1677724884_vanhoa14.jpg', '', 'Hàng xóm kế bên căn hộ của Fujimiya Amane chính là nữ sinh xinh đẹp nhất trường cậu, Shiina Mahiru.\r\n\r\nHọ vốn chẳng có mối liên hệ nào cho đến một ngày mưa tầm tã, Amane tình nguyện đưa chiếc ô của mình cho cô bạn hàng xóm xinh đẹp tựa thiên thần, cả hai đã bắt đầu tương tác với nhau theo một cách kì quặc.\r\n\r\nChẳng thể chịu được lối sinh hoạt cẩu thả khi sống một mình của Amane, Mahiru đã quyết định sẽ chăm sóc cậu từ những điều nhỏ nhất.\r\n\r\nMột Mahiru thiếu thốn sự gắn kết với gia đình đang dần mở lòng hơn, cùng một Amane hay tự ti đang ngày một đổi thay theo chiều hướng tích cực. Khoảng cách giữa hai con người không chút thành thật ấy đang từng bước thu hẹp lại...\r\n\r\nĐây là câu chuyện tình ngọt ngào với cô gái nhà bên tuy lạnh lùng nhưng thật đáng yêu đã được ủng hộ nhiệt tình trên trang Shousetsuka ni Narou.', 1, 29, 'Nhà Xuất Bản Kim Đồng', 'NXB Kim Đồng', 'Saekisan, Hanekoto'),
(38, 'V015', 'Thám Tử Đã Chết - Tập 1 ', '95000', 30, '1677724871_vanhoc15.jpg', '', 'Kimizuka Kimihiko, một học sinh bình thường, tuy vậy, bản thân lại mắc một thể chất đặc biệt luôn thu hút những rắc rối ập tới. Đi trên đường thì bị lôi kéo vào một đám đông phiền phức, đi vào trong hẻm thì bắt gặp cảnh giao dịch mờ ám, bản thân luôn vô tình tìm thấy hiện trường án mạng đầu tiên khiến cho cảnh sát gặp quen mặt phải nghi ngờ. Ngày hôm ấy, thể chất thu hút rắc rối kia tiếp tục hoành hành, khiến cậu không hiểu vì lý do gì lại ở trên một chuyến bay ra nước ngoài cùng một chiếc va ly khả nghi. Đấy cũng là thời điểm câu hỏi của người tiếp viên vang lên trong đầu cậu. Một tình huống nhạy cảm đã xảy ra trên chiếc máy bay này, khiến người tiếp viên cần cứu viện từ một thám tử. Và đấy cũng là lần đầu tiên, Kimizuka gặp cô ấy, người con gái tự xưng là thám tử bậc thầy với ngoại hình xinh đẹp tựa giấc mộng trưa hè, Siesta. Vì quá ngỡ ngàng, cậu đã ngỏ lời và được vị thám tử ấy mời làm trợ thủ.', 1, 29, 'Thái Hòa', 'NXB Hà Nội', 'Nigozyu'),
(39, 'V016', 'Làm Bạn Thân Có Khó Không - Tập 1', '105000', 23, '1677722954_vanhoc16.jpg', '', 'Bạn thân của tôi – Hinomori Ryuuga chính là một “Nhân vật chính đích thực”. Đầu tiên, cậu ta không bao giờ nói về quá khứ. Tiếp theo, cậu ta thường xuyên bỏ học. Mỗi khi quay trở về là lại có máu chảy từ khóe miệng, quần áo rách tả tơi (có lẽ là do chiến đấu với kẻ địch của thế giới này). Và rồi, xung quanh Ryuuga còn có rất nhiều người đẹp. Những lúc đó chính là sân khấu dành cho tôi.\r\n\r\n“Này Ryuuga! Tại sao cậu lại quen Yukimiya-san (người đẹp) hả!”. Góp phần giúp cho cuộc sống thường ngày của Ryuuga sôi động hơn chính là cách sống của tôi – Kobayashi Ichirou – một “bạn thân chuyên nghiệp”! Câu chuyện tình cảm hài được mang đến bởi Best friend Kobayashi – trợ diễn mạnh nhất – bắt đầu!!', 1, 29, 'AZ Việt Nam', 'NXB Thế Giới', 'Yasushi DATE'),
(40, 'K001', 'Nghĩ Giàu & Làm Giàu (Tái Bản 2020)', '77000 ', 64, '1677722778_kinhte1.jpg', '', 'Think and Grow Rich - Nghĩ giàu và làm giàu là cuốn sách đầu tiên đưa ra triết lý thành đạt - một triết lý đầy đủ và toàn diện về thành công của cá nhân, đồng thời cung cấp cho bạn phương pháp để tạo một ý thức thành công cũng như đưa ra kế hoạch sơ bộ và chi tiết để đạt được thành công đó. Các bí quyết thành công được đề cập đến trong cuốn sách này có thể được đúc kết ngắn gọn: tất cả bắt nguồn từ cách nghĩ.\r\n\r\nDo đó, cuốn sách này không chỉ thay đổi những điều bạn nghĩ mà còn có thể thay đổi cả cách nghĩ của bạn; không dừng lại ở việc chỉ ra cho bạn thấy bạn phải làm gì mà còn vạch cho bạn biết phải làm điều đó như thế nào để đạt được khát vọng của mình. Ra mắt bạn đọc với phiên bản cao cấp lần này, cuốn sách thay đổi nhiều về hình thức thiết kế bìa cứng, màu nâu đen, chữ vàng ánh kim dập nổi sang trọng, tinh tế.', 1, 33, 'FIRST NEWS', 'NXB Tổng Hợp TPHCM', 'Napoleon Hill'),
(41, 'K002', 'Dốc Hết Trái Tim (Tái Bản 2018)', '115500 ', 20, '1677723610_kinhte2.jpg', '', 'Dốc hết trái tim là một quyển sách hay dành cho doanh nhân và mọi giám đốc hay lãnh đạo công ty. Tác giả đồng thời là người sáng lập của thương hiệu Starbucks, chia sẻ câu chuyện đầy cảm hứng về cuộc đời của ông từ khi còn đi học đến khi trở thành CEO của một thương hiệu nổi tiếng trên toàn thế giới. Độc giả sẽ tìm thấy nơi đây bài học về quản trị rất tuyệt vời, mà giá trị của nó vẫn còn vững vàng trong hiện tại. Những kinh nghiệm của tác giả về xây dựng một công ty có trách nhiệm xã hội đối với nhân viên, với cộng đồng và đối với môi trường sẽ cuốn hút lan truyền cảm hứng đến độc giả để họ áp dụng nó vào cuộc sống và công việc kinh doanh của mình.\r\nTrong Dốc hết trái tim, CEO Howard Schultz chỉ ra các nguyên tắc định hình nên hiện tượng Starbucks, chia sẻ những tri thức mà ông đúc kết được từ cuộc hành trình biến cà phê ngon thành một phần tất yếu của trải nghiệm Mỹ. Các nhà tiếp thị, các nhà quản lý, và các doanh nhân sẽ khám phá ra cách biến lòng đam mê thành lợi nhuận trong cuốn biên niên ký của một công ty \"đã làm thay đổi mọi thứ... từ khẩu vị của chúng ta, ngôn ngữ của chúng ta cho đến bộ mặt của toàn khu Main Street\".', 1, 33, 'NXB Trẻ', 'NXB Trẻ', 'Howard Schultz , Dori Jones'),
(42, 'a1012', 'Vật lý', '30000', 1, '1677999525_15.png', '123jjdo', 'jo2ke1', 1, 34, 'Nhà Xuất Bản Kim Đồng', 'Kim đồng 2', 'Andrea Hirata'),
(43, 'V017', 'The Journey Of Youth - Chưa Kịp Lớn Đã Phải Trưởng Thành', '55300', 34, '1678437550_vanhoc17.jpg', '', '<p><strong>S&Aacute;CH SONG NGỮ &ldquo;THE JOURNEY OF YOUTH&rdquo; &ndash; CHƯA KỊP LỚN Đ&Atilde; PHẢI TRƯỞNG TH&Agrave;NH</strong></p>\r\n\r\n<p>1 cuốn s&aacute;ch gi&uacute;p bạn vừa học ngoại ngữ, vừa cổ vũ bản th&acirc;n nỗ lực mỗi ng&agrave;y!</p>\r\n\r\n<p>D&agrave;nh cho bạn - đứa trẻ chưa kịp lớn đ&atilde; phải trưởng th&agrave;nh&hellip;</p>\r\n\r\n<p>Đứa trẻ n&agrave;o cũng từng kh&aacute;t khao trở th&agrave;nh người lớn với những mơ mộng về tương lai. Khi lớn l&ecirc;n rồi, mới biết thế giới ngo&agrave;i kia rộng lớn, t&agrave;n khốc đến nhường n&agrave;o.&nbsp;</p>\r\n\r\n<p>Cuốn s&aacute;ch &ldquo;The Journey of Youth&rdquo; l&agrave; những l&aacute; thư được viết bằng 2 thứ tiếng Anh - Việt, nhắn nhủ tới bạn tr&ecirc;n đoạn đường gập ghềnh nhất của tuổi trẻ. Nếu bạn đang c&oacute; ch&uacute;t lạc l&otilde;ng v&agrave; hoang mang, h&atilde;y để &ldquo;The Journey of Youth&rdquo; ở b&ecirc;n, gửi tặng bạn đ&ocirc;i điều kh&iacute;ch lệ.&nbsp;</p>\r\n\r\n<p>Đặc biệt, với phần song ngữ Anh - Việt, s&aacute;ch như cuốn từ điển thu nhỏ về tuổi trẻ, thanh xu&acirc;n m&agrave; bạn c&oacute; thể mang theo b&ecirc;n m&igrave;nh. Vừa trau dồi ngoại ngữ, n&acirc;ng cấp bản th&acirc;n, vừa vỗ về bạn mỗi khi ch&ecirc;nh v&ecirc;nh, mơ hồ về tương lai ph&iacute;a trước.</p>\r\n\r\n<p>Cuốn s&aacute;ch &ldquo;THE JOURNEY OF YOUTH&rdquo; đ&atilde; từng được Timebooks xuất bản bản tiếng Việt với t&ecirc;n gọi &quot;Chưa kịp lớn đ&atilde; phải trưởng th&agrave;nh&rdquo;, cuốn s&aacute;ch đ&atilde; t&aacute;i bản 5 lần v&agrave; li&ecirc;n tục trở th&agrave;nh cuốn s&aacute;ch best seller trong nhiều th&aacute;ng sau khi ph&aacute;t h&agrave;nh.</p>\r\n', 1, 29, 'Công Ty Cổ Phần Time Books', 'Dân Trí', 'Tớ là Mây'),
(44, 'V018', 'Chuyện Kể Rằng Có Nàng Và Tôi', '50400', 24, '1678437722_vanhoc18.jpg', '', '<p>Đối với những người trẻ được sống như &yacute; kh&ocirc;ng phải l&uacute;c n&agrave;o cũng dễ d&agrave;ng, đặc biệt với những người đ&atilde; phải trải qua một qu&atilde;ng thời gian kh&oacute; khăn rồi mới c&oacute; thể t&igrave;m được con người thật của m&igrave;nh, l&agrave; ch&iacute;nh m&igrave;nh. Những c&acirc;u chuyện t&igrave;nh của họ c&oacute; nhiều dang dở v&igrave; những mặc cảm, r&agrave;o cản, khao kh&aacute;t được l&agrave;m điều m&igrave;nh muốn, gắn b&oacute; với người m&igrave;nh y&ecirc;u thương cả đời l&agrave; c&aacute;c mong ước nhỏ trong l&ograve;ng. Để rồi khi kh&ocirc;ng thể gi&atilde;i b&agrave;y c&ugrave;ng ai, họ mang những điều thầm k&iacute;n thổi v&agrave;o những vần thơ nơi chỉ c&oacute; những &ldquo;c&acirc;u chuyện về n&agrave;ng v&agrave; t&ocirc;i&rdquo;.</p>\r\n\r\n<p>&ldquo;Chuyện kể rằng c&oacute; n&agrave;ng v&agrave; t&ocirc;i&rdquo; l&agrave; cuốn s&aacute;ch nhỏ với những &aacute;ng thơ nhẹ nh&agrave;ng, l&atilde;ng mạn thể hiện mối giao h&ograve;a đẹp đẽ trong t&acirc;m hồn những người con g&aacute;i. T&igrave;nh y&ecirc;u của họ vượt tr&ecirc;n tất thảy mọi định kiến, chỉ c&ograve;n lại l&agrave; những cảm x&uacute;c dạt d&agrave;o, v&ocirc; tận. Trong những c&acirc;u thơ đ&ocirc;i khi họ l&agrave; những người l&atilde;ng du c&ocirc; đơn bước ch&acirc;n qua đ&aacute;m đ&ocirc;ng tranh c&atilde;i ồn &agrave;o v&agrave; lu&ocirc;n khao kh&aacute;t t&igrave;m kiếm hạnh ph&uacute;c.</p>\r\n\r\n<p>Rồi đ&ocirc;i khi lại đưa ta v&agrave;o những cung bậc h&acirc;n hoan, hạnh ph&uacute;c của t&igrave;nh y&ecirc;u, sự bao dung của những tr&aacute;i tim ngập tr&agrave;n nắng ấm, vượt qua mọi &aacute;nh nh&igrave;n xa lạ của x&atilde; hội, m&atilde;i khiến ta muốn đắm ch&igrave;m trong đ&oacute;.</p>\r\n\r\n<p>Dưới ng&ograve;i b&uacute;t uyển chuyển, sắc sảo của nhiều t&aacute;c giả, &ldquo;Chuyện kể rằng c&oacute; n&agrave;ng v&agrave; t&ocirc;i&rdquo; kh&ocirc;ng chỉ l&agrave; một cuốn s&aacute;ch về t&igrave;nh y&ecirc;u dang dở, bao cảm x&uacute;c mật ngọt t&aacute;n tỉnh của những n&agrave;ng đồng t&iacute;nh nữ m&agrave; c&ograve;n l&agrave; những bản ng&atilde;, niềm tin v&agrave; hy vọng, đủ ngọt ng&agrave;o để quyến rũ t&acirc;m hồn độc giả.</p>\r\n\r\n<p>Cuốn s&aacute;ch gi&uacute;p bạn gỡ bỏ những bức tường th&agrave;nh định kiến để đắm m&igrave;nh trong t&igrave;nh y&ecirc;u nhiệm m&agrave;u, v&igrave; ai rồi cũng c&oacute; quyền hạnh ph&uacute;c trong thế giới nhỏ của họ d&ugrave; l&agrave; giới t&iacute;nh n&agrave;o đi chăng nữa.</p>\r\n', 1, 29, 'Skybooks', 'NXB Phụ Nữ Việt Nam', 'Nhiều Tác Giả'),
(45, 'V019', 'Hành Trình Của Elaina - Tập 12', '69000', 18, '1678438023_vanhoc19.jpg', '', '<p>Ở một xứ sở xa x&ocirc;i, c&oacute; một c&ocirc; b&eacute; ph&ugrave; thủy th&iacute;ch ngao du thi&ecirc;n hạ. T&ecirc;n c&ocirc; l&agrave; Elaina, biệt danh &quot;ph&ugrave; thủy x&aacute;m&quot;.<br />\r\n<br />\r\nTrong h&agrave;nh tr&igrave;nh trải d&agrave;i th&uacute; vị của m&igrave;nh, Elaina đ&atilde; gặp gỡ rất nhiều người v&agrave; phi&ecirc;u lưu đến nhiều v&ugrave;ng đất tr&ecirc;n thế giới.<br />\r\n<br />\r\nMột vương quốc m&agrave; chỉ chấp nhận ph&aacute;p sư, một g&atilde; khổng lồ m&ecirc; cơ bắp, một thiếu ni&ecirc;n muốn cứu người y&ecirc;u khỏi lưỡi h&aacute;i Tử thần, một c&ocirc; c&ocirc;ng ch&uacute;a bị bỏ mặc giữa th&agrave;nh phố hoang t&agrave;n đổ n&aacute;t, v&agrave; c&acirc;u chuyện của ch&iacute;nh c&ocirc; ph&ugrave; thuỷ nhỏ vẫn đang chờ được kể...<br />\r\n<br />\r\nTừ khi gặp gỡ những nh&acirc;n vật lạ thường v&agrave; trải nghiệm những khoảnh khắc tươi đẹp của họ, đến tận b&acirc;y giờ, cuộc sống của c&ocirc; ph&ugrave; thuỷ nhỏ vẫn kh&ocirc;ng ngừng xoay quanh c&acirc;u chuyện của những cuộc gặp gỡ v&agrave; chia ly.<br />\r\n<br />\r\n&ldquo;Kh&ocirc;ng sao đ&acirc;u. D&ugrave; g&igrave; t&ocirc;i cũng l&agrave; kh&aacute;ch lữ h&agrave;nh m&agrave;. T&ocirc;i phải mau đi th&ocirc;i.&rdquo;<br />\r\nV&acirc;ng, c&ocirc; ph&ugrave; thủy đ&oacute; ch&iacute;nh l&agrave; t&ocirc;i.​</p>\r\n', 1, 29, 'XYZ', 'Dân Trí', 'Jougi Shiraishi'),
(46, 'V020', 'Người Đua Diều (Tái Bản 2018)', '76300', 29, '1678438147_vanhoc20.jpg', '', '<p>&ldquo;C&acirc;u chuyện về hai cậu b&eacute; từng l&agrave; bạn của nhau ở Afghanistan, cũng ch&iacute;nh l&agrave; một t&aacute;c phẩm phi thường về văn h&oacute;a.&rdquo;</p>\r\n\r\n<p><em>- San Francisco Chronicle</em></p>\r\n\r\n<p>&ldquo;Một tiểu thuyết đẹp đẽ&hellip; nằm trong số những t&aacute;c phẩm được viết ra tinh tế v&agrave; khơi gợi nhất cho tới giờ ph&uacute;t n&agrave;y&hellip; Một c&acirc;u chuyện l&agrave;m cảm động tr&aacute;i tim về một t&igrave;nh bạn kh&aacute;c thường&hellip; Đ&oacute; cũng l&agrave; một t&aacute;c phẩm thuyết phục kỳ lạ, về mối quan hệ mong manh giữa cha v&agrave; con, giữa con người v&agrave; thần th&aacute;nh, giữa c&aacute; nh&acirc;n v&agrave; đất nước. Huyết thống c&ugrave;ng sự thủy chung đ&atilde; l&agrave; sợi d&acirc;y nối buộc c&acirc;u chuyện của họ v&agrave;o một trong số những cuốn s&aacute;ch trữ t&igrave;nh, cảm động v&agrave; bất ngờ nhất của năm.&rdquo;</p>\r\n\r\n<p><em>- The Denver Post</em></p>\r\n\r\n<p>&ldquo;Một bức họa rực rỡ về Afghanistan ba mươi năm trước.&rdquo;</p>\r\n\r\n<p>-&nbsp;<em>The Wall Street Journal</em></p>\r\n\r\n<p>&ldquo;Đ&acirc;y l&agrave; một trong những c&acirc;u chuyện sẽ ở lại trong t&acirc;m tr&iacute; bạn trong nhiều năm nữa. Những chủ đề văn học v&agrave; nh&acirc;n sinh đ&atilde; tạo n&ecirc;n một cấu tr&uacute;c tiểu thuyết tuyệt vời: t&igrave;nh y&ecirc;u, danh dự, tội lỗi, sợ h&atilde;i v&agrave; cứu chuộc.&rdquo;</p>\r\n\r\n<p><em>- Isabel Allende</em></p>\r\n\r\n<p>&ldquo;Hosseini đ&atilde; d&ugrave;ng tới cả sự dịu d&agrave;ng lẫn nỗi kinh ho&agrave;ng; giấc mơ California v&agrave; cơn &aacute;c mộng Kabul c&oacute; vai tr&ograve; ngang nhau&hellip; Một truyện kể cuốn h&uacute;t, một ngụ ng&ocirc;n s&acirc;u sắc.&rdquo;&nbsp;</p>\r\n\r\n<p>-&nbsp;<em>Globe and Mail</em></p>\r\n', 1, 29, 'Nhã Nam', 'NXB Phụ Nữ ', 'Khaled Hosseini'),
(47, 'K003', '5 Điểm Mù Trong Kinh Doanh Nhà Hàng', '152100', 73, '1678438402_kinhte3.jpg', '', '<p>Cuốn s&aacute;ch l&agrave; bản đ&uacute;c kết những kinh nghiệm l&atilde;nh đạo doanh nghiệp qu&yacute; gi&aacute; cũng như c&aacute;ch thức điều h&agrave;nh nh&agrave; h&agrave;ng của Tilman Fertitta - &ldquo;chủ nh&agrave; hảng gi&agrave;u c&oacute; nhất tr&ecirc;n thế giới&rdquo; (theo Forbes). Với h&atilde;ng chục năm kinh nghiệm trong nền c&ocirc;ng nghiệp nh&agrave; h&agrave;ng, kh&aacute;ch sạn, giải tr&iacute; v&agrave; cờ bạc, t&aacute;c giả đ&atilde; chỉ ra năm điểm m&ugrave; c&oacute; thể giết chết doanh nghiệp nhưng thường bị c&aacute;c nh&agrave; quản l&yacute; bỏ qua, đ&oacute; l&agrave;:</p>\r\n\r\n<p>1. L&ograve;ng hiểu kh&aacute;ch<br />\r\n2. Hiểu r&otilde; những chỉ số của doanh nghiệp<br />\r\n3. Quy tắc 95:5<br />\r\n4. Nhin thấy cơ hội, nắm bắt cơ hội<br />\r\n5. &Aacute;p dụng quy tắc l&atilde;nh đạo v&agrave;o cuộc sống.</p>\r\n\r\n<p>Tilman Fertitta l&agrave; m&ocirc;t doanh nh&acirc;n xuất sắc to&agrave;n diện v&agrave; được xem l&agrave; l&atilde;nh đạo thế giới trong nền c&ocirc;ng nghiệp nh&agrave; h&agrave;ng, kh&aacute;ch sạn, giải tr&iacute; v&agrave; cờ bạc. Forbes gọi &ocirc;ng l&agrave; &ldquo;chủ nh&agrave; h&agrave;ng gi&agrave;u c&oacute; nhất tr&ecirc;n thế giới.&rdquo; Fertitta l&agrave; ng&ocirc;i sao trong chương tr&igrave;nh truyền h&igrave;nh thực tế của ri&ecirc;ng m&igrave;nh tr&ecirc;n k&ecirc;nh CNBC, Bi/ion Doliar Buyer, v&agrave; cũng l&agrave; chủ sở hữu duy nhất của c&ocirc;ng ty Fertitta Entertainment, quản l&yacute; chuỗi nh&agrave; h&agrave;ng Landry&#39;s kh&ocirc;ng lồ, s&ograve;ng bạc Golden Nugget v&agrave; chuỗi kh&aacute;ch sạn, đội b&oacute;ng rỗ Houston Rockets th&iacute; đấu tại giải b&oacute;ng rỗ nh&agrave; nghề nước Mỹ NBA.<br />\r\nTập trung v&agrave;o giải quyết những điểm mủ nảy sẽ tạo cơ hội cho doanh nghiệp ph&aacute;t triển v&agrave; bứt ph&aacute; l&ecirc;n tầm cao mới.</p>\r\n', 1, 33, 'Alpha Books', 'Công Thương', 'Tilman Fertitta'),
(48, 'K004', 'Chết Vì Chứng Khoán', '139300', 45, '1678438554_kinhte4.jpg', '', '<p>Đ&aacute;p ứng nhu cầu ng&agrave;y c&agrave;ng tăng trong việc t&igrave;m hiểu về cổ phần, cổ phiếu, chứng kho&aacute;n, chiến lược đầu tư của độc giả, Alpha Books chọn mua bản quyền dịch v&agrave; xuất bản bộ s&aacute;ch về đầu tư chứng kho&aacute;n bao gồm: Gi&agrave;u từ chứng kho&aacute;n; Trở th&agrave;nh thi&ecirc;n t&agrave;i chứng kho&aacute;n; Cổ phiếu thường - lợi nhuận phi thường; Hồi ức của một thi&ecirc;n t&agrave;i chứng kho&aacute;n; Phố Wall - Một Las Vegas kh&aacute;c; Quy tắc số 1: đầu tư th&agrave;nh c&ocirc;ng chỉ với 15 ph&uacute;t mỗi tuần; Tr&ecirc;n đỉnh Phố Wall&hellip;</p>\r\n\r\n<p>Cuốn s&aacute;ch Chết v&igrave; chứng kho&aacute;n: Jess Livermore - C&acirc;u chuyện về nh&agrave; đầu tư chứng kho&aacute;n vĩ đại nhất m&agrave; c&aacute;c bạn đang cầm trong tay nằm trong loạt s&aacute;ch n&agrave;y, l&agrave; về cuộc đời của Jesse Livermore, một trong những nh&agrave; kinh doanh chứng kho&aacute;n th&agrave;nh c&ocirc;ng nhất thế giới, nhưng ch&iacute;nh &ocirc;ng cũng phải đ&oacute;n nhận một kết cục hết sức bi thảm: gia đ&igrave;nh tan vỡ, t&agrave;i ch&iacute;nh ph&aacute; sản v&agrave; tự s&aacute;t trong tuyệt vọng. Cuốn s&aacute;ch sẽ đem lại rất nhiều b&agrave;i học bổ &iacute;ch, những kinh nghiệm chắt lọc từ thăng trầm trong cuộc đời &ocirc;ng hẳn sẽ c&oacute; gi&aacute; trị v&agrave; hữu &iacute;ch đối với độc giả v&agrave; c&aacute;c nh&agrave; đầu tư chứng kho&aacute;n của Việt Nam h&ocirc;m nay.</p>\r\n\r\n<p>Được tuyển chọn từ những bộ s&aacute;ch về đầu tư, t&agrave;i ch&iacute;nh v&agrave; chứng kho&aacute;n nổi tiếng nhất của nhiều t&aacute;c giả t&ecirc;n tuổi h&agrave;ng đầu thế giới, hy vọng c&aacute;c cuốn s&aacute;ch n&agrave;y sẽ trở th&agrave;nh một m&oacute;n qu&agrave; gi&aacute; trị v&agrave; hữu &iacute;ch cho tất cả c&aacute;c độc giả quan t&acirc;m.</p>\r\n', 1, 33, 'Alpha Books', 'NXB Lao Động', 'Richard Smitten'),
(49, 'K005', ' Người Bán Hàng Vĩ Đại Nhất Thế Giới', '113960', 52, '1678438706_kinhte5.jpg', '', '<p>&quot;Người B&aacute;n H&agrave;ng Vĩ Đại Nhất Thế Giới&quot; kể về c&acirc;u chuyện của Hafid, một cậu b&eacute; chăn lạc đ&agrave; ngh&egrave;o, ở Jerusalem thời cổ đại.&nbsp; Xuất th&acirc;n l&agrave; một cậu nh&oacute;c tr&ocirc;ng lạc đ&agrave; cho đo&agrave;n bu&ocirc;n của Pathros, nhưng với quyết t&acirc;m đổi đời, muốn cải thiện vị tr&iacute; của m&igrave;nh trong cuộc sống, Hafid lu&ocirc;n nu&ocirc;i ước mơ được trở th&agrave;nh người b&aacute;n h&agrave;ng. Cậu tin chỉ như thế m&igrave;nh mới c&oacute; cơ hội trở n&ecirc;n gi&agrave;u c&oacute; v&agrave; th&agrave;nh c&ocirc;ng.</p>\r\n\r\n<p>Giống như nhiều người theo đuổi đam m&ecirc; kh&aacute;c, trong suốt h&agrave;nh tr&igrave;nh ấy đ&ocirc;i l&uacute;c Hafid đ&atilde; nghĩ đến việc quay trở về với c&ocirc;ng việc chăn lạc đ&agrave;, kiếm những đồng xu lẻ sống qua ng&agrave;y. Biết bao nhi&ecirc;u mối suy tư nghi ngờ, thương tiếc bản th&acirc;n đ&atilde; nảy ra trong t&acirc;m tr&iacute; v&agrave; bủa v&acirc;y lấy cậu, nhưng với &yacute; ch&iacute; v&agrave; quyết t&acirc;m, cuối c&ugrave;ng Hafid cũng trở th&agrave;nh người b&aacute;n h&agrave;ng t&agrave;i năng nhất l&uacute;c bấy giờ.</p>\r\n\r\n<p>B&iacute; mật n&agrave;o đ&atilde; l&agrave;m n&ecirc;n th&agrave;nh c&ocirc;ng? Đ&oacute; ch&iacute;nh l&agrave; những nguy&ecirc;n tắc được một thương nh&acirc;n gi&agrave;u c&oacute; v&agrave; th&agrave;nh c&ocirc;ng truyền lại v&agrave; Hafid đ&atilde; giữ kỹ trong 10 cuộn giấy da trong suốt hơn ba thập kỉ qua. Với Hafid, những thứ giản dị n&agrave;y c&ograve;n qu&yacute; hơn cả kim cương. Nhờ &aacute;p dụng những nguy&ecirc;n tắc trong cuộn giấy da đ&oacute; m&agrave; Hafid được mệnh danh l&agrave; &ldquo;Người b&aacute;n h&agrave;ng vĩ đại nhất thế giới&rdquo;. Tuy nhi&ecirc;n, s&aacute;ch kh&ocirc;ng chỉ dạy bạn c&aacute;ch&nbsp;&ldquo;b&aacute;n h&agrave;ng&rdquo;, m&agrave; n&oacute; c&ograve;n l&agrave; một cuốn s&aacute;ch dạy bạn l&agrave;m người, gi&uacute;p bạn trở th&agrave;nh người&nbsp;&ldquo;vĩ đại nhất&rdquo;&nbsp;trong lĩnh vực m&agrave; bạn chọn.</p>\r\n', 1, 33, 'FIRST NEWS', 'NXB Tổng Hợp TPHCM', 'Og Mandino'),
(50, 'K006', 'Nghệ Thuật Bán Hàng Của Người Do Thái (Tái Bản 2020)', '61600', 38, '1678438847_kinhte6.jpg', '', '<p>Được mệnh danh l&agrave; &quot;quốc gia khởi nghiệp&quot;, Israel l&agrave; nơi sản sinh ra nhiều doanh nh&acirc;n th&agrave;nh c&ocirc;ng trong nhiều lĩnh vực v&agrave; ở mọi quốc gia. Để l&agrave;m được điều n&agrave;y, người Do Th&aacute;i đ&atilde; tiếp thu v&agrave; thấm nhuần những triết l&yacute; v&agrave; c&ocirc;ng cụ để tạo dựng uy t&iacute;n, x&acirc;y dựng v&agrave; duy tr&igrave; một cộng đồng kh&aacute;ch h&agrave;ng trung th&agrave;nh, thực hiện marketing s&aacute;ng tạo v&agrave; tối đa h&oacute;a doanh số cho c&ocirc;ng ty của m&igrave;nh &ndash; những phương ph&aacute;p m&agrave; người Do Th&aacute;i đ&atilde; &aacute;p dụng từ rất l&acirc;u. Tất cả những điều n&agrave;y sẽ được t&aacute;c giả Yaniv Zaid chia sẻ v&agrave; l&yacute; giải trong quyển s&aacute;ch Nghệ thuật b&aacute;n h&agrave;ng của người Do Th&aacute;i.</p>\r\n\r\n<p>Qua những mẩu chuyện nhỏ v&agrave; nhiều v&iacute; dụ thực tế trong quyển s&aacute;ch n&agrave;y, bạn đọc sẽ lần lượt t&igrave;m được c&acirc;u trả lời cho những c&acirc;u hỏi m&agrave; một chuy&ecirc;n gia marketing, một nh&acirc;n vi&ecirc;n b&aacute;n h&agrave;ng hay bất kỳ ai muốn n&acirc;ng cao kỹ năng thương thuyết đều c&oacute; thể quan t&acirc;m, chẳng hạn như &quot;L&agrave;m thế n&agrave;o để b&aacute;n được sản phẩm v&agrave; dịch vụ với gi&aacute; cao ngay cả trong một thị trường b&atilde;o h&ograve;a?&quot;, &quot;L&agrave;m sao để th&uacute;c đẩy người kh&aacute;c mua h&agrave;ng của bạn kể cả khi họ kh&ocirc;ng c&oacute; &yacute; định đ&oacute;?&quot;, &quot;L&agrave;m thế n&agrave;o để quảng b&aacute; v&agrave; b&aacute;n h&agrave;ng hiệu quả hơn đối thủ cạnh tranh?&quot;... Khi đọc quyển s&aacute;ch n&agrave;y, bạn sẽ thấy rằng những ch&acirc;n l&yacute; v&agrave; triết l&yacute; th&agrave;nh c&ocirc;ng kh&ocirc;ng nằm ở đ&acirc;u xa m&agrave; hiện diện v&agrave; được ứng dụng ngay trong đời sống hằng ng&agrave;y, trong những lần bạn tự hỏi &quot;Điểm kh&aacute;c biệt giữa một b&oacute; hoa cưới v&agrave; một b&oacute; hoa b&igrave;nh thường l&agrave; g&igrave;?&quot; hay &quot;Tại sao người ta thường phục vụ bữa ăn nhẹ trong một sự kiện?&quot;.</p>\r\n', 1, 33, 'FIRST NEWS', 'NXB Tổng Hợp TPHCM', 'Yaniv Zaid'),
(51, 'K007', 'Bí Mật Tư Duy Triệu Phú (Tái Bản 2021)', '75600', 44, '1678438960_kinhte7.jpg', '', '<p>Trong cuốn s&aacute;ch n&agrave;y&nbsp;<strong>T. Harv Eker&nbsp;</strong>sẽ tiết lộ những b&iacute; mật tại sao một số người lại đạt được những th&agrave;nh c&ocirc;ng vượt bậc, được số phận ban cho cuộc sống sung t&uacute;c, gi&agrave;u c&oacute;, trong khi một số người kh&aacute;c phải chật vật, vất vả mới c&oacute; một cuộc sống qua ng&agrave;y. Bạn sẽ hiểu được nguồn gốc sự thật v&agrave; những yếu tố quyết định th&agrave;nh c&ocirc;ng, thất bại để rồi &aacute;p dụng, thay đổi c&aacute;ch suy nghĩ, l&ecirc;n kế hoạch rồi t&igrave;m ra c&aacute;ch l&agrave;m việc, đầu tư, sử dụng nguồn t&agrave;i ch&iacute;nh của bạn theo hướng hiệu quả nhất.</p>\r\n\r\n<p>Cuốn s&aacute;ch d&agrave;nh cho những ai c&ograve;n loay hoay muốn t&igrave;m đường đến sự gi&agrave;u c&oacute; v&agrave; th&agrave;nh c&ocirc;ng. &ldquo;B&iacute; mật tự duy triệu ph&uacute;&rdquo; mang đến nhiều tư duy mới cho người đọc về c&aacute;ch suy nghĩ của người gi&agrave;u hay c&aacute;ch suy nghĩ để trở n&ecirc;n gi&agrave;u c&oacute;.</p>\r\n\r\n<p>Trong cuốn s&aacute;ch, Eker liệt k&ecirc; 17 c&aacute;ch thức m&agrave; c&aacute;c kế hoạch chi tiết t&agrave;i ch&iacute;nh của những người gi&agrave;u kh&aacute;c với người ngh&egrave;o v&agrave; tầng lớp trung lưu. Một chủ đề được x&aacute;c định trong s&aacute;ch n&agrave;y l&agrave; loại bỏ c&aacute;c suy nghĩ đổ lỗi cho sự thất bại. Eker lập luận rằng: Người gi&agrave;u tin &quot;T&ocirc;i tạo ra cuộc sống của t&ocirc;i&quot;, c&ograve;n những người ngh&egrave;o cho rằng &quot;Cuộc sống sẽ tự nhi&ecirc;n đến với t&ocirc;i&quot;; người gi&agrave;u tập trung v&agrave;o c&aacute;c cơ hội trong khi người ngh&egrave;o tập trung v&agrave;o những trở ngại; v&agrave; những người gi&agrave;u ngưỡng mộ những người gi&agrave;u c&oacute; v&agrave; th&agrave;nh c&ocirc;ng kh&aacute;c trong khi người d&acirc;n ngh&egrave;o ghen tị, bực tức trước những người th&agrave;nh c&ocirc;ng v&agrave; gi&agrave;u c&oacute;.</p>\r\n', 1, 33, 'FIRST NEWS', 'NXB Tổng Hợp TPHCM', 'T Harv Eker'),
(52, 'K008', 'Nhà Đầu Tư Thông Minh (Tái Bản 2020)', '149250', 51, '1678439127_kinhte8.jpg', '', '<p>L&agrave; nh&agrave; tư vấn&nbsp;đầu tư&nbsp;vĩ đại nhất của thế kỷ 20, Benjamin Graham đ&atilde; giảng dạy v&agrave; truyền cảm hứng cho nhiều người tr&ecirc;n khắp thế giới.&nbsp;Triết l&yacute; &ldquo;đầu tư theo gi&aacute; trị&ldquo; của Graham, bảo vệ nh&agrave; đầu tư khỏi những sai lầm lớn&nbsp;v&agrave; dạy anh ta ph&aacute;t triển c&aacute;c chiến lược d&agrave;i hạn, đ&atilde; khiến&nbsp;<em>Nh&agrave; đầu tư th&ocirc;ng minh</em><em>&nbsp;</em>trở th&agrave;nh cẩm nang của thị trường chứng kho&aacute;n kể từ lần xuất bản đầu ti&ecirc;n v&agrave;o năm 1949.</p>\r\n\r\n<p>Trải qua năm th&aacute;ng, diễn biến thị trường đ&atilde; chứng minh t&iacute;nh s&aacute;ng suốt trong c&aacute;c chiến lược của Graham. Trong khi vẫn giữ lại to&agrave;n vẹn văn bản ban đầu của Graham, ấn phẩm t&aacute;i bản n&agrave;y bổ sung th&ecirc;m b&igrave;nh luận cập nhật của k&yacute; giả chuy&ecirc;n về t&agrave;i ch&iacute;nh nổi tiếng Jason Zweig. C&aacute;i nh&igrave;n của Zweig bao qu&aacute;t hiện thực của thị trường ng&agrave;y nay, vạch ra sự tương tự giữa những v&iacute; dụ của Graham v&agrave; c&aacute;c t&iacute;t b&aacute;o về t&agrave;i ch&iacute;nh hiện nay, gi&uacute;p bạn đọc c&oacute; sự hiểu biết kỹ lưỡng hơn về c&aacute;ch thức &aacute;p dụng c&aacute;c nguy&ecirc;n tắc của Graham.</p>\r\n\r\n<p>Sống động v&agrave; cần thiết,&nbsp;<em>Nh&agrave; đầu tư th&ocirc;ng minh&nbsp;</em>l&agrave; cuốn s&aacute;ch quan trọng nhất m&agrave; bạn c&oacute; dịp đọc về c&aacute;ch thức đạt được c&aacute;c mục ti&ecirc;u t&agrave;i ch&iacute;nh của m&igrave;nh.</p>\r\n', 1, 33, 'Nhã Nam', 'NXB Thế Giới', 'Benjamin Graham'),
(53, 'KNS001', 'Sức Mạnh Tiềm Thức (Tái Bản 2021)', '89500', 41, '1678439372_KNS1.jpg', '', '<p>L&agrave; một trong những quyển s&aacute;ch về nghệ thuật sống nhận được nhiều lời ngợi khen v&agrave; b&aacute;n chạy nhất mọi thời đại,&nbsp;<strong>Sức Mạnh Tiềm Thức</strong>&nbsp;đ&atilde; gi&uacute;p h&agrave;ng triệu độc giả tr&ecirc;n thế giới đạt được những mục ti&ecirc;u quan trọng trong đời chỉ bằng một c&aacute;ch đơn giản l&agrave; thay đổi tư duy.</p>\r\n\r\n<p><strong>Sức Mạnh Tiềm Thức</strong>&nbsp;giới thiệu v&agrave; giải th&iacute;ch c&aacute;c phương ph&aacute;p tập trung t&acirc;m thức nhằm xo&aacute; bỏ những r&agrave;o cản tiềm thức đ&atilde; ngăn ch&uacute;ng ta đạt được những điều m&igrave;nh mong muốn.</p>\r\n\r\n<p><strong>Sức Mạnh Tiềm Thức</strong>&nbsp;kh&ocirc;ng những c&oacute; khả năng truyền cảm hứng cho người đọc, m&agrave; n&oacute; c&ograve;n rất thực tế v&igrave; được minh hoạ bằng những v&iacute; dụ sinh động trong cuộc sống hằng ng&agrave;y. Từ đ&oacute;, Sức Mạnh Tiềm Thức gi&uacute;p độc giả vận dụng năng lực tr&iacute; tuệ phi thường tiềm ẩn troing mỗi người để tạo dựng sự tự tin, x&acirc;y dựng c&aacute;c mối quan hệ ho&agrave; hợp, đạt được th&agrave;nh c&ocirc;ng trong sự nghiệp, vượt qua những nỗi sợ h&atilde;i v&agrave; &aacute;m ảnh, xua đi những th&oacute;i quen ti&ecirc;u cực, v&agrave; thậm ch&iacute; c&ograve;n hướng dẫn c&aacute;ch ta chữa l&agrave;nh những vết thương về thể chất cũng như t&acirc;m hồn để c&oacute; sự b&igrave;nh an, hạnh ph&uacute;c trọn vẹn trong cuộc sống.</p>\r\n', 1, 35, 'FIRST NEWS', 'NXB Tổng Hợp TPHCM', 'Joseph Murphyc'),
(54, 'KNS002', 'Đời Ngắn Đừng Ngủ Dài (Tái Bản 2018)', '56250', 33, '1678439515_KNS2.jpg', '', '<p>&ldquo;Mọi lựa chọn đều gi&aacute; trị. Mọi bước đi đều quan trọng. Cuộc sống vẫn diễn ra theo c&aacute;ch của n&oacute;, kh&ocirc;ng phải theo c&aacute;ch của ta. H&atilde;y ki&ecirc;n nhẫn. Tin tưởng. H&atilde;y giống như người thợ cắt đ&aacute;, đều đặn từng nhịp, ng&agrave;y qua ng&agrave;y. Cuối c&ugrave;ng, một nh&aacute;t cắt duy nhất sẽ ph&aacute; vỡ tảng đ&aacute; v&agrave; lộ ra vi&ecirc;n kim cương. Người tr&agrave;n đầy nhiệt huyết v&agrave; tận t&acirc;m với việc m&igrave;nh l&agrave;m kh&ocirc;ng bao giờ bị chối bỏ. Sự thật l&agrave; thế.&rdquo;</p>\r\n\r\n<p>Bằng những lời chia sẻ thật ngắn gọn, dễ hiểu về những trải nghiệm v&agrave; suy ngẫm trong đời, Robin Sharma tiếp tục phong c&aacute;ch viết của &ocirc;ng từ cuốn s&aacute;ch&nbsp;Điều vĩ đại đời thường&nbsp;để mang đến cho độc giả những b&agrave;i viết như lời t&acirc;m sự, vừa ch&acirc;n th&agrave;nh vừa s&acirc;u sắc.</p>\r\n', 1, 35, 'NXB Trẻ', 'NXB Trẻ', 'Robin Sharma'),
(55, 'DL02', 'Balo Sakos Neo Gibraltar', '2900000', 22, '1683169133_balo16.png', '', '<p><strong>CHI TIẾT SẢN PHẨM</strong></p>\r\n\r\n<p><strong>Balo cần k&eacute;o du lịch Sakos NEO GIBRALTAR c&oacute; thiết kế hiện đại với chất liệu chống nước tuyệt hảo</strong></p>\r\n\r\n<ul>\r\n	<li>Thiết kế quen thuộc với h&igrave;nh hộp chữ nhất đứng nhưng tạo điểm nhấn &ldquo;trượt n&oacute;c&rdquo; ở phần đỉnh lạ mắt.</li>\r\n	<li>Balo cần k&eacute;o Sakos được l&agrave;m từ c&aacute;c loại chất liệu Polyester, Nylon, PVC, EVA. Trọng lượng nhẹ, bền, chống trầy xước, va đập, chống thấm nước v&agrave; dễ d&agrave;ng vệ sinh</li>\r\n	<li>Ngăn mở rộng với d&acirc;y k&eacute;o s&aacute;t mặt lưng, tăng cường thể t&iacute;ch l&ecirc;n 20% so với trạng th&aacute;i b&igrave;nh thường.</li>\r\n	<li>B&ecirc;n trong trang bị vải l&oacute;t 3 t&iacute;nh năng: chống thấm, h&uacute;t ẩm, kh&aacute;ng khuẩn.</li>\r\n</ul>\r\n\r\n<p><strong>Ph&acirc;n ngăn tiện lợi khiến Balo cần k&eacute;o du lịch Sakos NEO GIBRALTAR ph&ugrave; hợp đi học, đi l&agrave;m hoặc du lịch</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Ngăn laptop chống sốc l&ecirc;n đến 17 inch được t&iacute;ch hợp b&ecirc;n trong ngăn ch&iacute;nh tiện lợi.</li>\r\n	<li>Ngăn ch&iacute;nh rộng r&atilde;i c&oacute; thể đựng quần &aacute;o cho chuyến đi cả tuần.</li>\r\n	<li>Hệ thống c&aacute;c ngăn phụ k&iacute;ch thước lớn &ndash; nhỏ đa dạng tiết kiệm thời sắp xếp đồ d&ugrave;ng ngăn nắp.</li>\r\n	<li>C&aacute;c ngăn phụ b&ecirc;n h&ocirc;ng với độ co gi&atilde;n nhất định dễ d&agrave;ng cất &ndash; lấy đồ d&ugrave;ng nhanh ch&oacute;ng.</li>\r\n</ul>\r\n', 1, 41, '', '35x23x54 cm', '2.8 kg'),
(56, 'DL01', 'Balo Du Lịch Stargo Ligero', '199000', 45, '1683169064_balo15.png', '', '<p><strong>CHI TIẾT SẢN PHẨM</strong></p>\r\n\r\n<p><strong>Balo du lịch Stargo Ligero sở hữu thiết kế tối giản, chất liệu trượt nước vượt trội</strong></p>\r\n\r\n<ul>\r\n	<li>Chất liệu polyester trượt nước, bền m&agrave;u, chống b&aacute;m bụi v&agrave; dễ d&agrave;ng vệ sinh khi bị d&iacute;nh bẩn.</li>\r\n	<li>B&ecirc;n trong trang bị vải l&oacute;t cao cấp, bảo vệ đồ d&ugrave;ng.</li>\r\n</ul>\r\n\r\n<p><strong>Ph&acirc;n ngăn tiện lợi khiến Stargo Ligero l&agrave; lựa chọn h&agrave;ng đầu</strong></p>\r\n\r\n<ul>\r\n	<li>Stargo Ligero&nbsp;gồm 1 ngăn ch&iacute;nh v&agrave; ngăn phụ tiện lợi.</li>\r\n	<li>C&aacute;c ngăn phụ k&iacute;ch thước lớn &ndash; nhỏ đa dạng dễ d&agrave;ng sắp xếp đồ d&ugrave;ng ngăn nắp.</li>\r\n</ul>\r\n\r\n<p><strong>C&ocirc;ng nghệ may gấp m&eacute;p viền tạo phom cứng c&aacute;p cho balo du lịch Stargo Ligero</strong></p>\r\n\r\n<ul>\r\n	<li>C&ocirc;ng nghệ may gấp m&eacute;p viền tinh tế, bền bỉ c&ugrave;ng thời gian.</li>\r\n	<li>Quai x&aacute;ch v&agrave; quai đeo được may kĩ c&agrave;ng, chắc chắn.</li>\r\n</ul>\r\n\r\n<p><strong>Tinh tế trong từng chi tiết gi&uacute;p balo Stargo Ligero bảo vệ v&oacute;c d&aacute;ng người d&ugrave;ng</strong></p>\r\n\r\n<ul>\r\n	<li>Stargo Ligero&nbsp;trang bị quai đeo to bản với đệm m&uacute;t d&agrave;y, chống hằn, chống đau vai, mỏi cổ. Dễ d&agrave;ng điều chỉnh chiều d&agrave;i d&acirc;y đeo với chốt tăng &ndash; đơ tiện lợi.</li>\r\n</ul>\r\n', 1, 41, '', '26x14x44 cm', '0.25 kg'),
(57, 'BL12', 'Balo Laptop Sakos Matrix', '3130000', 72, '1683166639_balo12.png', '', '<p><strong>Chất liệu cao cấp mang đến đột ph&aacute; trong thiết kế v&agrave; sự tiện &iacute;ch</strong></p>\r\n\r\n<ul>\r\n	<li>Matrix c&oacute; thiết kế h&igrave;nh hộp chữ nhật đứng ti&ecirc;u chuẩn với sự kết hợp th&ocirc;ng minh giữa PU v&agrave; Polyester tạo n&ecirc;n sự tinh tế mang đậm phong c&aacute;ch ch&acirc;u &Acirc;u những năm 80.</li>\r\n	<li>Hệ thống d&acirc;y k&eacute;o chống nước, đầu k&eacute;o da &ecirc;m &aacute;i mang lại sự an to&agrave;n v&agrave; cảm gi&aacute;c dễ chịu cho da tay.</li>\r\n	<li>Điểm nhấn độc đ&aacute;o của Matrix l&agrave; đai bảo vệ đ&iacute;nh nam ch&acirc;m ẩn v&agrave;o b&ecirc;n trong ở ngăn phụ mặt trước, vừa an to&agrave;n vừa tăng t&iacute;nh thẩm mỹ cho d&ograve;ng balo vốn dĩ đ&atilde; sang trọng chạm đến sự ho&agrave;n mỹ về diện mạo b&ecirc;n ngo&agrave;i.</li>\r\n	<li>Ngo&agrave;i thiết kế mang t&iacute;nh đột ph&aacute; v&agrave; c&ocirc;ng nghệ hiện đại khi t&iacute;ch hợp cổng USB, Sakos c&ograve;n tinh tế bố tr&iacute; đai cố định v&agrave;o cần k&eacute;o vali ở mặt sau balo mang đến sự sang trọng, tiện lợi trong những chuyến bay.</li>\r\n</ul>\r\n\r\n<p><strong>Ngăn chứa tiện lợi t&iacute;ch hợp ngăn laptop l&ecirc;n đến 15.6 inch</strong></p>\r\n\r\n<ul>\r\n	<li>Bộ sưu tập balo Matrix gồm 2 ngăn ch&iacute;nh v&agrave; nhiều ngăn phụ tiện lợi. Ngăn laptop được thiết kế s&aacute;t lưng, chống sốc, an to&agrave;n tuyệt đối cho c&aacute;c d&ograve;ng laptop c&oacute; k&iacute;ch thước từ 15.6&Prime; trở xuống.</li>\r\n	<li>Ngăn ch&iacute;nh c&oacute; thể t&iacute;ch tương đối lớn gi&uacute;p bạn c&oacute; thể mang theo h&agrave;nh l&yacute; trong những chuyến du lịch ngắn ng&agrave;y m&agrave; kh&ocirc;ng cần sự hỗ trợ của sản phẩm hay phụ kiện n&agrave;o kh&aacute;c. Đ&acirc;y l&agrave; nơi th&iacute;ch hợp cho quần &aacute;o, s&aacute;ch vở, t&agrave;i liệu hay c&aacute;c vật dụng cần thiết. Ch&iacute;nh sự linh hoạt trong sức chứa của Matrix khiến bạn c&oacute; thể tự tin trong mọi h&agrave;nh tr&igrave;nh d&ugrave; l&agrave; đi học, đi l&agrave;m, c&ocirc;ng t&aacute;c hay du lịch.</li>\r\n</ul>\r\n', 1, 40, '', '29.5x18x42.5 cm', '1 kg');
INSERT INTO `tbl_sanpham` (`id_sanpham`, `masp`, `tensanpham`, `giasp`, `soluong`, `hinhanh`, `tomtat`, `noidung`, `tinhtrang`, `id_danhmucsp`, `chatlieu`, `kichthuoc`, `trongluong`) VALUES
(58, 'BL11', 'Balo laptop Sakos Nitro', '1849000', 56, '1683166538_balo11.png', '', '<p><strong>CHI TIẾT SẢN PHẨM</strong></p>\r\n\r\n<p><strong>Thiết kế ngăn chống trộm th&ocirc;ng minh an to&agrave;n tuyệt đối</strong></p>\r\n\r\n<ul>\r\n	<li>Sakos Nitro c&oacute; thiết kế hiện đại, sang trọng kết hợp c&ugrave;ng m&agrave;u đen đầy c&aacute; t&iacute;nh c&oacute; thể d&ugrave;ng trong nhiều trường hợp kh&aacute;c nhau như đi học, đi l&agrave;m hay du lịch, đi chơi&hellip;</li>\r\n	<li>D&ograve;ng balo th&ocirc;ng minh Nitro c&oacute; ngăn k&iacute;n được giấu sau lưng, miệng kh&oacute;a được che đậy bởi vai đeo, chống trộm tối ưu, bảo vệ tuyệt đối cho đồ đạc b&ecirc;n trong.</li>\r\n	<li>Ngo&agrave;i ngăn ch&iacute;nh được che bởi hai quai đeo th&igrave; ngăn phụ nằm s&aacute;t phần đ&aacute;y balo cũng được giấu bởi bề mặt balo ph&iacute;a trước tạo cảm gi&aacute;c y&ecirc;n t&acirc;m cho người d&ugrave;ng. Ngăn n&agrave;y th&iacute;ch hợp mang theo tiền bạc hoặc c&aacute;c m&oacute;n đồ gi&aacute; trị như đồng hồ hay trang sức.</li>\r\n</ul>\r\n\r\n<p><strong>Chống nước hiệu quả</strong></p>\r\n\r\n<ul>\r\n	<li>Balo th&ocirc;ng minh Nitro c&oacute; cấu tạo từ Polyester trượt nước hiệu quả. Nước sẽ nhanh ch&oacute;ng trượt d&agrave;i v&agrave; rơi khỏi bề mặt balo ngay sau khi tiếp x&uacute;c.</li>\r\n	<li>Mặt ch&iacute;nh diện balo được trang bị lớp đệm m&uacute;t PU v&agrave; phủ vải chống thấm b&ecirc;n ngo&agrave;i vừa c&oacute; t&aacute;c dụng giữ được form d&aacute;ng chuẩn vừa mang lại sự sang trọng cho d&ograve;ng balo cao cấp n&agrave;y.</li>\r\n	<li>B&ecirc;n trong balo được l&oacute;t nylon với t&iacute;nh năng chống thấm vượt trội, đảm bảo an to&agrave;n cho c&aacute;c thiết bị điện tử b&ecirc;n trong d&ugrave; sử dụng trong điều kiện m&ocirc;i trường ẩm ướt.</li>\r\n</ul>\r\n', 1, 40, '', '17x32x47 cm', '0.94 kg'),
(59, 'BL10', 'Balo Laptop Sakos Cyber', '1175000', 61, '1683166153_balo10.png', '', '<p><strong>CHI TIẾT SẢN PHẨM</strong></p>\r\n\r\n<p><strong>Balo laptop Cyber thiết kế mạnh mẽ, chất liệu trượt nước vượt trội</strong></p>\r\n\r\n<ul>\r\n	<li>Chất liệu polyester trượt nước, bền m&agrave;u, chống b&aacute;m bụi v&agrave; dễ d&agrave;ng vệ sinh khi bị d&iacute;nh bẩn.</li>\r\n	<li>B&ecirc;n trong trang bị vải l&oacute;t 3 t&iacute;nh năng: chống thấm, h&uacute;t ẩm, kh&aacute;ng khuẩn.</li>\r\n	<li>Thiết kế viền chỉ đồng m&agrave;u tạo điểm nhấn độc đ&aacute;o, nam t&iacute;nh.</li>\r\n	<li>Logo Sakos tinh tế, khẳng định chất lượng sản phẩm v&agrave; gi&aacute; trị thương hiệu được chuyển nhượng độc quyền từ USA.</li>\r\n</ul>\r\n\r\n<p><strong>Ph&acirc;n ngăn tiện lợi khiến cho d&ograve;ng balo laptop Sakos Cyber ph&ugrave; hợp đi học, đi l&agrave;m v&agrave; mục đ&iacute;ch du lịch</strong></p>\r\n\r\n<ul>\r\n	<li>Balo laptop Cyber gồm 1 ngăn ch&iacute;nh, 1 ngăn laptop chống sốc ri&ecirc;ng biệt v&agrave; nhiều ngăn phụ tiện lợi.</li>\r\n	<li>Ngăn laptop ri&ecirc;ng biệt trang bị đệm EVA &ecirc;m &aacute;i, chống sốc cho c&aacute;c d&ograve;ng m&aacute;y t&iacute;nh x&aacute;ch tay 15.6&rdquo;.</li>\r\n	<li>Ngăn ch&iacute;nh rộng r&atilde;i th&iacute;ch hợp cho quần &aacute;o, t&agrave;i liệu, s&aacute;ch vở&hellip; cho chuyến du lịch 3-5 ng&agrave;y.</li>\r\n	<li>Hệ thống nhiều ngăn phụ với k&iacute;ch thước lớn nhỏ đa dạng dễ d&agrave;ng sắp xếp đồ d&ugrave;ng ngăn nắp.</li>\r\n	<li>Hai ngăn phụ mặt h&ocirc;ng trang bị t&uacute;i lưới th&ocirc;ng tho&aacute;ng kết hợp đai vải chắc chắn dễ d&agrave;ng cất &ndash; lấy đồ d&ugrave;ng.</li>\r\n</ul>\r\n', 1, 40, '', '34x21x46 cm', '1 kg'),
(60, 'BL09', ' Balo Laptop Sakos Layla', '1165000', 47, '1683165728_balo13.png', '', '<p><strong>CHI TIẾT SẢN PHẨM</strong></p>\r\n\r\n<p><strong>Balo laptop Layla sở hữu thiết kế hiện đại, chất liệu cao cấp với những t&iacute;nh năng ưu việt</strong></p>\r\n\r\n<ul>\r\n	<li>Chất liệu polyester trượt nước, bền m&agrave;u, chống b&aacute;m bụi v&agrave; dễ d&agrave;ng vệ sinh khi bị d&iacute;nh bẩn.</li>\r\n	<li>B&ecirc;n trong trang bị vải l&oacute;t 3 t&iacute;nh năng: chống thấm, h&uacute;t ẩm, kh&aacute;ng khuẩn.</li>\r\n	<li>Thiết kế h&igrave;nh hộp với phần nắp tr&ecirc;n mở bẹt một g&oacute;c 45 độ ph&aacute; c&aacute;ch, mới lạ.</li>\r\n	<li>Ngăn phụ mặt trước trang tr&iacute; viền nổi m&agrave;u l&agrave; điểm nhấn độc đ&aacute;o.</li>\r\n	<li>Logo Sakos tinh tế khẳng định chất lượng sản phẩm được sản xuất tr&ecirc;n c&ocirc;ng nghệ độc quyền Sakostyle (USA).</li>\r\n</ul>\r\n\r\n<p><img alt=\"\" src=\"https://sakos.vn/Data/Sites/1/media/2-sp/50/29.png\" /><strong>Ph&acirc;n ngăn tiện lợi khiến Layla xứng đ&aacute;ng l&agrave; d&ograve;ng balo laptop ch&iacute;nh h&atilde;ng ph&ugrave; hợp đi học, đi l&agrave;m hoặc du lịch</strong></p>\r\n\r\n<ul>\r\n	<li>Balo laptop Layla gồm 1 ngăn ch&iacute;nh v&agrave; nhiều ngăn phụ tiện lợi.</li>\r\n	<li>Ngăn ch&iacute;nh rộng r&atilde;i với ngăn laptop chống sốc 15.6 inch.</li>\r\n	<li>Kh&ocirc;ng gian c&ograve;n lại trong ngăn ch&iacute;nh kh&aacute; rộng r&atilde;i, đựng đồ d&ugrave;ng cho chuyến đi 3-5 ng&agrave;y.</li>\r\n	<li>Nhiều ngăn phụ k&iacute;ch thước lớn &ndash; nhỏ đa dạng dễ d&agrave;ng sắp xếp đồ d&ugrave;ng ngăn nắp.</li>\r\n	<li>Hai ngăn phụ b&ecirc;n h&ocirc;ng trang bị kh&oacute;a zip tiện lợi, cất &ndash; lấy đồ d&ugrave;ng nhanh ch&oacute;ng.</li>\r\n	<li>Đặc biệt, ngăn &ldquo;b&iacute; mật&rdquo; được giấu kỹ s&aacute;t lưng người d&ugrave;ng.</li>\r\n</ul>\r\n', 1, 40, '', '32x14x47 cm', '0.9 kg'),
(61, 'BL08', 'Balo laptop Sakos Epsilon', '419000', 55, '1683166804_balo14.png', '', '<p><strong>CHI TIẾT SẢN PHẨM</strong></p>\r\n\r\n<p><strong>Balo laptop Epsilon sở hữu thiết kế tối giản, chất liệu trượt nước vượt trội</strong></p>\r\n\r\n<ul>\r\n	<li>Chất liệu polyester trượt nước, bền m&agrave;u, chống b&aacute;m bụi v&agrave; dễ d&agrave;ng vệ sinh khi bị d&iacute;nh bẩn.</li>\r\n	<li>B&ecirc;n trong trang bị vải l&oacute;t 3 t&iacute;nh năng: chống thấm, h&uacute;t ẩm, kh&aacute;ng khuẩn.</li>\r\n	<li>Thiết kế h&igrave;nh hộp vu&ocirc;ng vức, bẹp g&oacute;c ở đỉnh tạo cảm gi&aacute;c nhẹ nh&agrave;ng ph&ugrave; hợp cho nam v&agrave; nữ.</li>\r\n	<li>Mặt trước trang tr&iacute; đai vải m&agrave;u tương phản, kh&aacute; mảnh h&igrave;nh g&oacute;c vu&ocirc;ng mới lạ, ph&aacute; bỏ giới hạn đối xứng.</li>\r\n	<li>Logo Sakos tinh tế khẳng định chất lượng sản phẩm v&agrave; gi&aacute; trị thương hiệu được chuyển nhượng độc quyền từ USA.</li>\r\n</ul>\r\n\r\n<p><strong>Ph&acirc;n ngăn tiện lợi khiến Epsilon xứng đ&aacute;ng l&agrave; d&ograve;ng balo laptop đẹp ph&ugrave; hợp đi học, đi l&agrave;m hoặc du lịch</strong></p>\r\n\r\n<ul>\r\n	<li>Balo laptop epsilon gồm 1 ngăn ch&iacute;nh v&agrave; nhiều ngăn phụ tiện lợi.</li>\r\n	<li>Ngăn ch&iacute;nh rộng r&atilde;i với ngăn laptop 14&rdquo; t&iacute;ch hợp th&ecirc;m đai cố định an to&agrave;n. Phần c&ograve;n lại trong ngăn ch&iacute;nh th&iacute;ch đựng quần &aacute;o, t&agrave;i liệu, s&aacute;ch vở&hellip; cho chuyến đi 3-5 ng&agrave;y.</li>\r\n	<li>Hệ thống c&aacute;c ngăn phụ k&iacute;ch thước lớn &ndash; nhỏ đa dạng dễ d&agrave;ng sắp xếp đồ d&ugrave;ng ngăn nắp.</li>\r\n</ul>\r\n', 1, 40, '', '27x13x40 cm', '0.7 kg'),
(62, 'BL07', 'Balo Laptop Stargo Vivaz', '499000', 41, '1683165541_balo07.png', '', '<p><strong>CHI TIẾT SẢN PHẨM</strong></p>\r\n\r\n<p><strong>Chất liệu trượt nước, dễ d&agrave;ng vệ sinh</strong></p>\r\n\r\n<ul>\r\n	<li>Bộ sưu tập balo thời trang laptop 14 inch Stargo Vivaz được l&agrave;m từ chất liệu polyester trượt nước, chống b&aacute;m bụi, dễ d&agrave;ng vệ sinh khi bị d&iacute;nh bẩn.</li>\r\n	<li>Trọng lượng balo thời trang laptop 14 inch Stargo Vivaz si&ecirc;u nhẹ &ndash; chỉ 0.6 kg, k&iacute;ch thước 28 x 13 x 41 cm.</li>\r\n	<li>C&ocirc;ng nghệ may gấp m&eacute;p viền với đường may tinh xảo vừa chắc chắn, bền bỉ vừa c&oacute; độ thẩm mỹ cao.</li>\r\n</ul>\r\n\r\n<p><strong>Thiết kế hiện đại đan xen với cổ điển tinh tế</strong></p>\r\n\r\n<ul>\r\n	<li>Vivaz được tạo n&ecirc;n bởi sự ph&aacute; c&aacute;ch trong thiết kế v&agrave; chọn lọc m&agrave;u sắc.</li>\r\n	<li>Vivaz c&oacute; 4 phi&ecirc;n bản m&agrave;u: Đen &ndash; X&aacute;m &ndash; Xanh &ndash; Đen.</li>\r\n	<li>D&acirc;y k&eacute;o zip nhựa dẻo đ&oacute;ng &ndash; mở &ecirc;m mượt.</li>\r\n	<li>Đầu k&eacute;o kim loại với l&aacute; k&eacute;o vải mềm mịn, bảo vệ da tay, tạo cảm gi&aacute;c dễ chịu khi sử dụng.</li>\r\n</ul>\r\n', 1, 40, '', '28x13x41 cm', '0.6 kg'),
(63, 'BL006', 'Balo Laptop Sakos Cybero', '1639000', 32, '1683165419_balo06.png', '', '<p><strong>CHI TIẾT SẢN PHẨM</strong></p>\r\n\r\n<p><strong>Balo laptop CYBERO c&oacute; thiết kế hiện đại với chất liệu chống nước tuyệt đối</strong></p>\r\n\r\n<ul>\r\n	<li>Thiết kế quen thuộc với h&igrave;nh hộp chữ nhất đứng nhưng tạo điểm nhấn &ldquo;trượt n&oacute;c&rdquo; ở phần đỉnh lạ mắt.</li>\r\n	<li>Chất liệu polyester phủ PU chống nước tuyệt đối, bền bỉ, dễ d&agrave;ng vệ sinh bằng khăn ẩm khi bị d&iacute;nh bẩn.</li>\r\n	<li>Ngăn mở rộng với d&acirc;y k&eacute;o s&aacute;t mặt lưng, tăng cường thể t&iacute;ch l&ecirc;n 20% so với trạng th&aacute;i b&igrave;nh thường.</li>\r\n	<li>B&ecirc;n trong trang bị vải l&oacute;t 3 t&iacute;nh năng: chống thấm, h&uacute;t ẩm, kh&aacute;ng khuẩn.</li>\r\n</ul>\r\n\r\n<p><strong>Ph&acirc;n ngăn tiện lợi khiến balo laptop CYBERO ph&ugrave; hợp đi học, đi l&agrave;m hoặc du lịch</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Ngăn laptop chống sốc 15.6 inch được t&iacute;ch hợp b&ecirc;n trong ngăn ch&iacute;nh tiện lợi.</li>\r\n	<li>Ngăn ch&iacute;nh rộng r&atilde;i c&oacute; thể đựng quần &aacute;o cho chuyến đi cả tuần.</li>\r\n	<li>Hệ thống c&aacute;c ngăn phụ k&iacute;ch thước lớn &ndash; nhỏ đa dạng tiết kiệm thời sắp xếp đồ d&ugrave;ng ngăn nắp.</li>\r\n	<li>Hai ngăn phụ b&ecirc;n h&ocirc;ng với độ co gi&atilde;n nhất định dễ d&agrave;ng cất &ndash; lấy đồ d&ugrave;ng nhanh ch&oacute;ng.</li>\r\n</ul>\r\n', 1, 40, '', '30x17x44 cm', '0.9 kg'),
(64, 'BL05', 'Balo Laptop Sakos Matura', '1749000', 33, '1683165281_Balo05.png', '', '<p><strong>CHI TIẾT SẢN PHẨM</strong></p>\r\n\r\n<p><strong>Balo laptop MATURA c&oacute; thiết kế hiện đại với chất liệu chống nước tuyệt đối</strong></p>\r\n\r\n<ul>\r\n	<li>Thiết kế quen thuộc với h&igrave;nh hộp chữ nhất đứng nhưng tạo điểm nhấn &ldquo;trượt n&oacute;c&rdquo; ở phần đỉnh lạ mắt.</li>\r\n	<li>Chất liệu polyester phủ PU chống nước tuyệt đối, bền bỉ, dễ d&agrave;ng vệ sinh bằng khăn ẩm khi bị d&iacute;nh bẩn.</li>\r\n	<li>Ngăn mở rộng với d&acirc;y k&eacute;o s&aacute;t mặt lưng, tăng cường thể t&iacute;ch l&ecirc;n 20% so với trạng th&aacute;i b&igrave;nh thường.</li>\r\n	<li>B&ecirc;n trong trang bị vải l&oacute;t 3 t&iacute;nh năng: chống thấm, h&uacute;t ẩm, kh&aacute;ng khuẩn.</li>\r\n</ul>\r\n\r\n<p><strong>Ph&acirc;n ngăn tiện lợi khiến balo laptop MATURA ph&ugrave; hợp đi học, đi l&agrave;m hoặc du lịch</strong></p>\r\n\r\n<ul>\r\n	<li>Ngăn laptop chống sốc 15.6 inch được t&iacute;ch hợp b&ecirc;n trong ngăn ch&iacute;nh tiện lợi.</li>\r\n	<li>Ngăn ch&iacute;nh rộng r&atilde;i c&oacute; thể đựng quần &aacute;o cho chuyến đi cả tuần.</li>\r\n	<li>Hệ thống c&aacute;c ngăn phụ k&iacute;ch thước lớn &ndash; nhỏ đa dạng tiết kiệm thời sắp xếp đồ d&ugrave;ng ngăn nắp.</li>\r\n	<li>Hai ngăn phụ b&ecirc;n h&ocirc;ng với độ co gi&atilde;n nhất định dễ d&agrave;ng cất &ndash; lấy đồ d&ugrave;ng nhanh ch&oacute;ng.</li>\r\n</ul>\r\n', 1, 40, '', '30x17x44 cm', '0.9 kg'),
(65, 'BL04', 'Balo Laptop Sakos Dily', '1529000', 17, '1683165166_balo04.png', '', '<p><strong>CHI TIẾT SẢN PHẨM</strong></p>\r\n\r\n<p><strong>Balo laptop Dily thiết kế mạnh mẽ với chất liệu cao cấp</strong></p>\r\n\r\n<ul>\r\n	<li>Thiết kế nam t&iacute;nh đầy cuốn h&uacute;t với điểm nhấn l&agrave; c&aacute;c mảng da đồng m&agrave;u sang trọng.</li>\r\n	<li>Chất liệu polyester trượt nước, bền m&agrave;u, chống b&aacute;m bụi v&agrave; dễ d&agrave;ng vệ sinh khi bị d&iacute;nh bẩn.</li>\r\n	<li>B&ecirc;n trong trang bị vải l&oacute;t 3 t&iacute;nh năng: chống thấm, h&uacute;t ẩm, kh&aacute;ng khuẩn.</li>\r\n	<li>Nhiều ngăn phụ mặt trước được bảo vệ bởi kh&oacute;a k&eacute;o vừa tiện lợi vừa mang t&iacute;nh thẩm mỹ cao.</li>\r\n	<li>Logo Sakos tinh tế khẳng định chất lượng sản phẩm v&agrave; gi&aacute; trị thương hiệu được chuyển nhượng độc quyền từ USA.</li>\r\n</ul>\r\n\r\n<p><strong>Ph&acirc;n ngăn tiện lợi khiến Dily xứng đ&aacute;ng l&agrave; d&ograve;ng balo laptop ch&iacute;nh h&atilde;ng ph&ugrave; hợp đi học, đi l&agrave;m hoặc du lịch</strong></p>\r\n\r\n<ul>\r\n	<li>Balo laptop Dily gồm ngăn ch&iacute;nh rộng r&atilde;i, ngăn laptop chống sốc v&agrave; nhiều ngăn phụ tiện &iacute;ch.</li>\r\n	<li>Ngăn laptop chống sốc 15.6 inch ri&ecirc;ng biệt.</li>\r\n	<li>Ngăn ch&iacute;nh rỗng r&atilde;i chứa được quần &aacute;o cho chuyến đi 3 &ndash; 5 ng&agrave;y.</li>\r\n	<li>Nhiều ngăn phụ với k&iacute;ch thước lớn &ndash; nhỏ kh&aacute;c nhau, dễ d&agrave;ng sắp xếp đồ d&ugrave;ng ngăn nắp.</li>\r\n	<li>Hai ngăn phụ b&ecirc;n h&ocirc;ng trang bị kh&oacute;a zip tiện lợi, cất &ndash; lấy đồ d&ugrave;ng nhanh ch&oacute;ng.</li>\r\n	<li>Đặc biệt, ngăn &ldquo;b&iacute; mật&rdquo; được giấu kỹ s&aacute;t lưng người d&ugrave;ng.</li>\r\n</ul>\r\n', 1, 40, '', '31x16x45 cm', '1.04 kg'),
(66, 'BL03', 'Balo Laptop Sakos Morale', '1690000', 31, '1683164366_balo03.png', '', '<p><strong>CHI TIẾT SẢN PHẨM</strong></p>\r\n\r\n<p><strong>Balo laptop Morale c&oacute; thiết kế hiện đại</strong></p>\r\n\r\n<ul>\r\n	<li>Thiết kế quen thuộc với h&igrave;nh hộp chữ nhất đứng nhưng tạo điểm nhấn &ldquo;trượt n&oacute;c&rdquo; ở phần đỉnh lạ mắt.</li>\r\n	<li>Chất liệu polyester phủ PU bền bỉ, dễ d&agrave;ng vệ sinh bằng khăn ẩm khi bị d&iacute;nh bẩn.</li>\r\n	<li>Ngăn mở rộng với d&acirc;y k&eacute;o s&aacute;t mặt lưng, tăng cường thể t&iacute;ch l&ecirc;n 20% so với trạng th&aacute;i b&igrave;nh thường.</li>\r\n	<li>B&ecirc;n trong trang bị vải l&oacute;t 3 t&iacute;nh năng: chống thấm, h&uacute;t ẩm, kh&aacute;ng khuẩn.</li>\r\n</ul>\r\n\r\n<p><strong>Ph&acirc;n ngăn tiện lợi khiến balo laptop Morale ph&ugrave; hợp đi học, đi l&agrave;m hoặc du lịch</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Ngăn laptop chống sốc 15.6 inch được t&iacute;ch hợp b&ecirc;n trong ngăn ch&iacute;nh tiện lợi.</li>\r\n	<li>Ngăn ch&iacute;nh rộng r&atilde;i c&oacute; thể đựng quần &aacute;o cho chuyến đi cả tuần.</li>\r\n	<li>Hệ thống c&aacute;c ngăn phụ k&iacute;ch thước lớn &ndash; nhỏ đa dạng tiết kiệm thời sắp xếp đồ d&ugrave;ng ngăn nắp.</li>\r\n	<li>Hai ngăn phụ b&ecirc;n h&ocirc;ng với độ co gi&atilde;n nhất định dễ d&agrave;ng cất &ndash; lấy đồ d&ugrave;ng nhanh ch&oacute;ng.</li>\r\n</ul>\r\n', 1, 40, '', '31 x 16 x 47 cm', '0.94 kg'),
(67, 'BL02', 'Balo Laptop Sakos Galaxy', '1045000', 22, '1683164591_balo2.jpg', '', '<p><strong>CHI TIẾT SẢN PHẨM</strong></p>\r\n\r\n<p><strong>Balo laptop chống sốc 15.6 inch Sakos Galaxy sở hữu thiết kế thời thượng</strong></p>\r\n\r\n<ul>\r\n	<li>Galaxy được l&agrave;m chất liệu vải trượt nước cao cấp kết hợp với kiểu d&aacute;ng thiết kế sang trọng mang đến cảm gi&aacute;c thoải m&aacute;i v&agrave; thời thượng mỗi khi sử dụng.</li>\r\n	<li>Logo Sakos được th&ecirc;u tỉ mỉ, tinh tế &ndash; biểu tượng khẳng định chất lượng sản phẩm v&agrave; gi&aacute; trị thương hiệu.</li>\r\n	<li>C&ocirc;ng nghệ may gấp m&eacute;p viền với đường chỉ tinh xảo vừa chắc chắn vừa mang t&iacute;nh thẩm mỹ cao.</li>\r\n</ul>\r\n\r\n<p><strong>Ph&acirc;n ngăn tiện lợi với ngăn laptop chống sốc 15.6 inch</strong></p>\r\n\r\n<ul>\r\n	<li>Sản phẩm c&oacute; 1 ngăn ch&iacute;nh v&agrave; 3 ngăn phụ.</li>\r\n	<li>Mặt trước l&agrave; 2 ngăn phụ nhỏ được thiết kế tinh tế, lắp d&acirc;y k&eacute;o c&oacute; logo thương hiệu Sakos như một minh chứng khẳng định chất lượng.</li>\r\n	<li>Ngăn phụ c&ograve;n lại được sắp xếp ở vị tr&iacute; chuyển tiếp giữa ngăn ch&iacute;nh v&agrave; 2 ngăn phụ, ph&ugrave; hợp đựng dụng cụ học tập hay những vật dụng phục vụ sinh hoạt.</li>\r\n	<li>Ngăn ch&iacute;nh được thiết kế vu&ocirc;ng vức, t&iacute;ch hợp th&ecirc;m &ldquo;bức v&aacute;ch&rdquo; l&oacute;t đệm &ecirc;m &aacute;i chống sốc cho laptop 15.6 inch.</li>\r\n	<li>Khoảng trống c&ograve;n lại trong ngăn ch&iacute;nh, l&agrave; nơi th&iacute;ch hợp cho s&aacute;ch vở hay quần &aacute;o.</li>\r\n	<li>Hai ngăn b&ecirc;n h&ocirc;ng với đai thun cố định tốt kết hợp vải lưới th&ocirc;ng tho&aacute;ng th&iacute;ch hợp với chai nước hoặc những vật dụng dễ d&agrave;ng cất &ndash; lấy nhanh ch&oacute;ng.</li>\r\n</ul>\r\n', 1, 40, '', '31 x 22 x 42 cm', '0.9 kg'),
(68, 'BL01', 'Balo Laptop Sakos Sonic', '1265000', 24, '1683164009_balo1.jpg', '', '<p><strong>CHI TIẾT SẢN PHẨM</strong></p>\r\n\r\n<p><strong>Balo laptop Sonic sở hữu thiết kế mạnh mẽ, chất liệu trượt nước vượt trội</strong></p>\r\n\r\n<ul>\r\n	<li>Chất liệu polyester trượt nước, bền m&agrave;u, chống b&aacute;m bụi v&agrave; dễ d&agrave;ng vệ sinh khi bị d&iacute;nh bẩn.</li>\r\n	<li>B&ecirc;n trong trang bị vải l&oacute;t 3 t&iacute;nh năng: chống thấm, h&uacute;t ẩm, kh&aacute;ng khuẩn.</li>\r\n	<li>Viền chỉ đồng m&agrave;u chắc chắn, mang t&iacute;nh thẩm mỹ cao nhầm tạo sự mạnh mẽ v&agrave; năng động cho balo.</li>\r\n	<li>Lỗ th&ocirc;ng kh&iacute; bọc kim loại hai b&ecirc;n gi&uacute;p tho&aacute;t kh&iacute;, h&uacute;t ẩm tối ưu.</li>\r\n	<li>Logo Sakos tinh tế, khẳng định chất lượng sản phẩm v&agrave; gi&aacute; trị thương hiệu được chuyển nhượng độc quyền từ USA.</li>\r\n</ul>\r\n\r\n<p><strong>Ph&acirc;n ngăn tiện lợi khiến cho d&ograve;ng balo nam Sonic ph&ugrave; hợp đi học, đi l&agrave;m v&agrave; mục đ&iacute;ch du lịch</strong></p>\r\n\r\n<ul>\r\n	<li>Balo laptop Sonic gồm 1 ngăn ch&iacute;nh, 1 ngăn laptop chống sốc ri&ecirc;ng biệt v&agrave; nhiều ngăn phụ tiện lợi.</li>\r\n	<li>Ngăn laptop ri&ecirc;ng biệt trang bị đệm EVA &ecirc;m &aacute;i, chống sốc cho c&aacute;c d&ograve;ng m&aacute;y t&iacute;nh x&aacute;ch tay 15.6&rdquo;.</li>\r\n	<li>Ngăn ch&iacute;nh rộng r&atilde;i th&iacute;ch hợp cho quần &aacute;o, t&agrave;i liệu, s&aacute;ch vở&hellip; cho chuyến du lịch 3-5 ng&agrave;y.</li>\r\n	<li>Hệ thống nhiều ngăn phụ với k&iacute;ch thước lớn nhỏ đa dạng dễ d&agrave;ng sắp xếp đồ d&ugrave;ng ngăn nắp.</li>\r\n	<li>Hai ngăn phụ mặt h&ocirc;ng trang bị kh&oacute;a zip an to&agrave;n, dễ d&agrave;ng cất &ndash; lấy.</li>\r\n</ul>\r\n', 1, 40, '', '33x20x45 cm', '1.1 kg');

